const EXTENSION_ID = 'aMonteSl.code-xr';

// Funci√≥n principal basada en tu ejemplo exacto
async function obtenerMetricasCodeXR() {
  const url = 'https://marketplace.visualstudio.com/_apis/public/gallery/extensionquery?api-version=3.0-preview.1';

  // Body de la petici√≥n, filtrando por "aMonteSl.code-xr" y pidiendo estad√≠sticas (flags = 256)
  const body = {
    filters: [
      {
        criteria: [
          {
            filterType: 7,
            value: 'aMonteSl.code-xr'  // Identificador completo de la extensi√≥n
          }
        ],
        pageNumber: 1,
        pageSize: 1
      }
    ],
    flags: 256 // IncludeStatistics (descargas, instalaciones, rating)
  };

  console.log('üîç Realizando petici√≥n exacta a la API para:', EXTENSION_ID);
  console.log('üìã Body de la petici√≥n:', JSON.stringify(body, null, 2));

  const response = await fetch(url, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Accept': 'application/json;api-version=3.0-preview.1'
    },
    body: JSON.stringify(body)
  });

  console.log('üì° Respuesta HTTP status:', response.status);

  if (!response.ok) {
    throw new Error(`Error al consultar API: ${response.status} ${response.statusText}`);
  }

  const data = await response.json();
  console.log('üìä Datos completos de la API:', data);

  const ext = data.results[0]?.extensions[0];
  if (!ext) {
    console.error('‚ùå La extensi√≥n Code-XR no fue encontrada en el Marketplace.');
    throw new Error('La extensi√≥n Code-XR no fue encontrada en el Marketplace.');
  }

  console.log('‚úÖ Extensi√≥n encontrada:', ext.extensionName);
  console.log('üìà Estad√≠sticas raw:', ext.statistics);

  // Normalizar estad√≠sticas a un objeto para extraer valores f√°cilmente
  const statsObj = {};
  for (const s of ext.statistics || []) {
    statsObj[s.statisticName] = s.value;
    console.log(`üìä Estad√≠stica: ${s.statisticName} = ${s.value}`);
  }

  console.log('üó∫Ô∏è Objeto de estad√≠sticas normalizado:', statsObj);

  const result = {
    totalInstalaciones: statsObj.installCount ?? statsObj.install ?? 0,
    promedioRating: statsObj.averageRating ?? 0,
    totalValoraciones: statsObj.ratingCount ?? 0,
    totalDescargas: statsObj.downloads ?? 0
  };

  console.log('üìà M√©tricas finales extra√≠das:', result);

  return result;
}

// Funci√≥n de adaptaci√≥n para mantener compatibilidad con el hook existente
