import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import '../App.css';

// ✨ CAMBIO: Usar PNG para el header
import CodeXRIconPNG from '../assets/logos/icon.png';

// ✨ NUEVO: Importar componente FAB
import { FAB } from '../components';

function KeyFeatures() {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('overview');
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // ✨ NUEVO: Datos para el carrusel de imágenes (placeholder)
  const carouselImages = [
    {
      id: 1,
      src: 'https://via.placeholder.com/800x500/3b82f6/ffffff?text=XR+Code+Visualization',
      caption: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore.',
      title: 'Immersive Code Exploration'
    },
    {
      id: 2,
      src: 'https://via.placeholder.com/800x500/06b6d4/ffffff?text=3D+Metrics+View',
      caption: 'Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo.',
      title: '3D Metrics Visualization'
    },
    {
      id: 3,
      src: 'https://via.placeholder.com/800x500/10b981/ffffff?text=AR+Code+Analysis',
      caption: 'Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.',
      title: 'Augmented Reality Analysis'
    },
    {
      id: 4,
      src: 'https://via.placeholder.com/800x500/f59e0b/ffffff?text=Team+Collaboration',
      caption: 'Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim.',
      title: 'Team Collaboration in XR'
    }
  ];

  // ✨ NUEVO: Carrusel automático
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prev) => (prev + 1) % carouselImages.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [carouselImages.length]);

  // ✨ NUEVO: Datos organizados por categorías
  const featureCategories = {
    overview: {
      title: 'Overview',
      icon: '🎯',
      features: [
        {
          title: 'Interactive Visualization',
          icon: '🔍',
          color: '#ff6b6b',
          description: 'Explore your code in an immersive 3D environment with dynamic visualizations.',
          highlights: ['Real-time 3D rendering', 'Interactive navigation', 'Dynamic feedback']
        },
        {
          title: 'Real-time Metrics',
          icon: '📊',
          color: '#4ecdc4',
          description: 'Instant analysis of complexity, code quality, and design patterns.',
          highlights: ['Cyclomatic complexity', 'Quality indicators', 'Performance hotspots']
        },
        {
          title: 'XR Experience',
          icon: '🥽',
          color: '#45b7d1',
          description: 'Compatible with AR/VR devices for total immersion.',
          highlights: ['WebXR compatibility', 'Multi-device support', 'Desktop fallback']
        }
      ]
    },
    integration: {
      title: 'Integration',
      icon: '⚡',
      features: [
        {
          title: 'Seamless VS Code Integration',
          icon: '🔧',
          color: '#96ceb4',
          description: 'One-click activation directly from your favorite editor.',
          highlights: ['Context menu integration', 'Hotkey support', 'Auto file analysis']
        },
        {
          title: 'Zero Configuration',
          icon: '🚀',
          color: '#feca57',
          description: 'No additional setup required - works out of the box.',
          highlights: ['Instant activation', 'Auto-detection', 'Smart defaults']
        }
      ]
    },
    collaboration: {
      title: 'Collaboration',
      icon: '🤝',
      features: [
        {
          title: 'Team Sharing',
          icon: '🔄',
          color: '#ff9ff3',
          description: 'Share XR visualizations with your team for better code reviews.',
          highlights: ['Shareable sessions', 'Team exploration', 'Export capabilities']
        },
        {
          title: 'Enhanced Communication',
          icon: '💬',
          color: '#a78bfa',
          description: 'Improve team communication with spatial code understanding.',
          highlights: ['Visual discussions', 'Shared context', 'Better reviews']
        }
      ]
    }
  };

  const goToImage = (index) => {
    setCurrentImageIndex(index);
  };

  const nextImage = () => {
    setCurrentImageIndex((prev) => (prev + 1) % carouselImages.length);
  };

  const prevImage = () => {
    setCurrentImageIndex((prev) => (prev - 1 + carouselImages.length) % carouselImages.length);
  };

  return (
    <div className="key-features-page">
      {/* Header */}
      <motion.header className="header">
        <div className="nav-container">
          <motion.div 
            className="logo-container"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => navigate('/')}
          >
            <img src={CodeXRIconPNG} alt="Code-XR" className="logo-icon" />
            <span className="logo-text">Code-XR</span>
          </motion.div>

          <nav className="nav-menu">
            <button 
              className="nav-link internal" 
              onClick={() => navigate('/')}
            >
              ← Back to Home
            </button>
            <div className="nav-separator"></div>
            <a 
              href="https://marketplace.visualstudio.com/items?itemName=aMonteSl.code-xr" 
              className="nav-link external"
              target="_blank"
              rel="noopener noreferrer"
            >
              <span className="nav-text">Install</span>
              <span className="external-icon">↗</span>
            </a>
            <a 
              href="https://github.com/aMonteSl/babiaxr-vscode" 
              className="nav-link external"
              target="_blank"
              rel="noopener noreferrer"
            >
              <span className="nav-text">GitHub</span>
              <span className="external-icon">↗</span>
            </a>
          </nav>
        </div>
      </motion.header>

      {/* Hero Section */}
      <motion.section className="key-features-hero">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <h1 className="page-title">
            <span className="brand">Key</span> Features
          </h1>
          <p className="page-subtitle">
            Discover how Code-XR transforms your development experience with cutting-edge XR technology
          </p>
        </motion.div>
      </motion.section>

      {/* ✨ NUEVO: Video Section */}
      <motion.section className="video-showcase">
        <div className="video-container">
          <motion.div
            className="video-wrapper"
            initial={{ opacity: 0, scale: 0.9 }}
            whileInView={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <div className="video-placeholder">
              <div className="video-placeholder-content">
                <div className="play-button">
                  <span>▶</span>
                </div>
                <h3>See Code-XR in Action</h3>
                <p>Watch how Code-XR transforms code analysis into an immersive experience</p>
                <div className="video-badge">Coming Soon</div>
              </div>
            </div>
          </motion.div>
        </div>
      </motion.section>

      {/* ✨ NUEVO: Image Carousel */}
      <motion.section className="image-carousel-section">
        <div className="carousel-container">
          <motion.h2 
            className="carousel-title"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            Experience Gallery
          </motion.h2>

          <div className="carousel-wrapper">
            <motion.button 
              className="carousel-nav prev"
              onClick={prevImage}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              ←
            </motion.button>

            <div className="carousel-content">
              <AnimatePresence mode="wait">
                <motion.div
                  key={currentImageIndex}
                  className="carousel-slide"
                  initial={{ opacity: 0, x: 100 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: -100 }}
                  transition={{ duration: 0.5 }}
                >
                  <div className="carousel-image-wrapper">
                    <img 
                      src={carouselImages[currentImageIndex].src} 
                      alt={carouselImages[currentImageIndex].title}
                      className="carousel-image"
                    />
                    <div className="image-overlay">
                      <h4>{carouselImages[currentImageIndex].title}</h4>
                    </div>
                  </div>
                  <div className="carousel-caption">
                    <p>{carouselImages[currentImageIndex].caption}</p>
                  </div>
                </motion.div>
              </AnimatePresence>
            </div>

            <motion.button 
              className="carousel-nav next"
              onClick={nextImage}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              →
            </motion.button>
          </div>

          <div className="carousel-indicators">
            {carouselImages.map((_, index) => (
              <button
                key={index}
                className={`indicator ${index === currentImageIndex ? 'active' : ''}`}
                onClick={() => goToImage(index)}
              />
            ))}
          </div>
        </div>
      </motion.section>

      {/* ✨ NUEVO: Features Tabs */}
      <motion.section className="features-tabs-section">
        <div className="tabs-container">
          <motion.div 
            className="tabs-header"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2>Detailed Features</h2>
            <div className="tabs-navigation">
              {Object.entries(featureCategories).map(([key, category]) => (
                <button
                  key={key}
                  className={`tab-button ${activeTab === key ? 'active' : ''}`}
                  onClick={() => setActiveTab(key)}
                >
                  <span className="tab-icon">{category.icon}</span>
                  <span className="tab-text">{category.title}</span>
                </button>
              ))}
            </div>
          </motion.div>

          <AnimatePresence mode="wait">
            <motion.div
              key={activeTab}
              className="tab-content"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.4 }}
            >
              <div className="features-grid-compact">
                {featureCategories[activeTab].features.map((feature, index) => (
                  <motion.div
                    key={feature.title}
                    className="feature-card-compact"
                    style={{ '--feature-color': feature.color }}
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.4, delay: index * 0.1 }}
                  >
                    <div className="feature-compact-header">
                      <div 
                        className="feature-compact-icon"
                        style={{ backgroundColor: `${feature.color}20`, color: feature.color }}
                      >
                        {feature.icon}
                      </div>
                      <h3>{feature.title}</h3>
                    </div>
                    <p className="feature-compact-description">{feature.description}</p>
                    <div className="feature-highlights">
                      {feature.highlights.map((highlight, i) => (
                        <span key={i} className="highlight-tag">
                          {highlight}
                        </span>
                      ))}
                    </div>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          </AnimatePresence>
        </div>
      </motion.section>

      {/* ✨ NUEVO: Technical Specifications */}
      <motion.section className="tech-specs">
        <div className="specs-container">
          <motion.h2 
            className="specs-title"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            Technical Specifications
          </motion.h2>

          <div className="specs-grid">
            <motion.div 
              className="spec-card"
              initial={{ opacity: 0, x: -30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: 0.1 }}
              viewport={{ once: true }}
            >
              <h4>🎮 XR Compatibility</h4>
              <ul>
                <li>WebXR 1.0 Standard</li>
                <li>Oculus Quest/Rift Support</li>
                <li>HTC Vive Compatible</li>
                <li>Mobile AR Ready</li>
              </ul>
            </motion.div>

            <motion.div 
              className="spec-card"
              initial={{ opacity: 0, x: 30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
            >
              <h4>⚙️ System Requirements</h4>
              <ul>
                <li>VS Code 1.60+</li>
                <li>Node.js 14+</li>
                <li>Modern WebGL Browser</li>
                <li>4GB RAM Minimum</li>
              </ul>
            </motion.div>

            <motion.div 
              className="spec-card"
              initial={{ opacity: 0, x: -30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: 0.3 }}
              viewport={{ once: true }}
            >
              <h4>📊 Supported Metrics</h4>
              <ul>
                <li>Cyclomatic Complexity</li>
                <li>Code Coverage</li>
                <li>Technical Debt</li>
                <li>Dependency Analysis</li>
              </ul>
            </motion.div>

            <motion.div 
              className="spec-card"
              initial={{ opacity: 0, x: 30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              viewport={{ once: true }}
            >
              <h4>🔧 Integration</h4>
              <ul>
                <li>Git Integration</li>
                <li>ESLint Support</li>
                <li>TypeScript Ready</li>
                <li>Multi-language Support</li>
              </ul>
            </motion.div>
          </div>
        </div>
      </motion.section>

      {/* CTA Section */}
      <motion.section className="key-features-cta">
        <motion.div
          className="cta-content"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <h2>Ready to Experience Code-XR?</h2>
          <p>
            Transform your code analysis workflow with immersive XR visualizations
          </p>
          <div className="cta-buttons">
            <a 
              href="https://marketplace.visualstudio.com/items?itemName=aMonteSl.code-xr"
              className="cta-button primary"
              target="_blank"
              rel="noopener noreferrer"
            >
              Install Extension
            </a>
            <button 
              className="cta-button secondary"
              onClick={() => navigate('/')}
            >
              View Demo
            </button>
          </div>
        </motion.div>
      </motion.section>

      {/* FAB */}
      <FAB />
    </div>
  );
}

export default KeyFeatures;
