import React from 'react';
import { motion } from 'framer-motion';
import { FeatureCard } from '../ui'; // ‚ú® NUEVO: Importar componente UI

const FeaturesSection = ({ goToSlide }) => {
  const features = [
    {
      icon: 'üîç',
      title: 'Interactive Visualization',
      description: 'Explore your code in 3D space with intuitive navigation and dynamic visual feedback.',
      color: '#ff6b6b'
    },
    {
      icon: 'üìä',
      title: 'Real-time Metrics',
      description: 'Instant analysis of complexity, quality indicators, and performance hotspots.',
      color: '#4ecdc4'
    },
    {
      icon: 'ü•Ω',
      title: 'XR Experience',
      description: 'Full compatibility with AR/VR devices for immersive code exploration.',
      color: '#45b7d1'
    },
    {
      icon: '‚ö°',
      title: 'Zero Configuration',
      description: 'Works out of the box with VS Code - no additional setup required.',
      color: '#96ceb4'
    },
    {
      icon: 'üîÑ',
      title: 'Live Updates',
      description: 'Real-time synchronization with your code changes and file modifications.',
      color: '#feca57'
    },
    {
      icon: 'ü§ù',
      title: 'Team Collaboration',
      description: 'Share XR visualizations with your team for enhanced code reviews.',
      color: '#ff9ff3'
    }
  ];

  return (
    <div className="slide features-slide" id="features">
      <motion.h2 
        className="section-title"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <span className="brand">Features</span> Overview
      </motion.h2>

      <motion.div 
        className="features-grid"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, delay: 0.2 }}
      >
        {features.map((feature, index) => (
          <FeatureCard
            key={feature.title}
            icon={feature.icon}
            title={feature.title}
            description={feature.description}
            color={feature.color}
            delay={0.1 + index * 0.1}
            className=""
          />
        ))}
      </motion.div>

      {/* Next Section Arrow */}
      <div 
        className="next-section-arrow next-section-arrow-spaced" 
        onClick={() => goToSlide(3)}
      >
        <div className="arrow-icon">‚Üì</div>
      </div>
    </div>
  );
};

export default FeaturesSection;
