import React, { useRef, useState, useEffect } from 'react';
import { motion, useScroll, useSpring, AnimatePresence } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import '../App.css';

// Importar hooks y utilidades
import { useExtensionStats } from '../hooks/useExtensionStats';
import { formatNumber, formatRating, formatVersion } from '../utils/formatNumbers';

// Import technology logos
import NodejsLogo from '../assets/logos/nodejs.svg';
import TypescriptLogo from '../assets/logos/typescript.svg';
import AframeLogo from '../assets/logos/aframe.png';
import BabiaXRLogo from '../assets/logos/babiaxr.png';
import PythonLogo from '../assets/logos/python.svg';
import WebXRLogo from '../assets/logos/webxr.png';

// Import Code-XR logos
import CodeXRIcon from '../assets/logos/icon.svg';  // ‚ú® MANTENER SVG para el hero     
import CodeXRIconPNG from '../assets/logos/icon.png'; // ‚ú® USAR PNG para el header

// ‚ú® NUEVO: Importar componente FAB
import { FAB } from '../components';

function Home() {
  const navigate = useNavigate();
  const containerRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [copied, setCopied] = useState(false);
  const [currentTextIndex, setCurrentTextIndex] = useState(0);
  const totalSlides = 6; // ‚ú® MANTENER: Home S√ç tiene 6 slides

  // Hook para obtener estad√≠sticas reales
  const { downloads, installs, rating, version, loading, error } = useExtensionStats();

  // ‚ú® DEBUGGING: Log de versi√≥n en tiempo real
  useEffect(() => {
    console.log('üè† Home Component - Version State:', {
      version,
      loading,
      error,
      formattedVersion: formatVersion(version)
    });
  }, [version, loading, error]);

  // Rotating phrases for subtitle
  const rotatingTexts = [
    "Visualize your code metrics in Augmented and Virtual Reality.",
    "Transform static code analysis into immersive XR exploration.",
    "Experience your code in XR or on-screen ‚Äî clear, structured, insightful.",
    "Step beyond the editor ‚Äî analyze code with spatial context in XR.",
    "From static views to immersive XR ‚Äî see what your code is really doing.",
    "Code metrics reimagined ‚Äî interactively, spatially, in XR."
  ];

  // Main scroll hook for the entire page
  const { scrollYProgress } = useScroll({
    target: containerRef,
    offset: ["start start", "end end"]
  });

  // Progress bar for scroll
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  // Effect to change text every 6 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTextIndex((prev) => (prev + 1) % rotatingTexts.length);
    }, 6000);

    return () => clearInterval(interval);
  }, [rotatingTexts.length]);

  // ‚ú® ARREGLAR: Detectar slide actual con mejor precisi√≥n
  useEffect(() => {
    let ticking = false;

    const handleScroll = () => {
      if (!ticking) {
        requestAnimationFrame(() => {
          const scrollTop = window.scrollY;
          const windowHeight = window.innerHeight;

          // ‚ú® MEJORAR: C√°lculo m√°s preciso del slide
          // Usar offset de 10% para mejor detecci√≥n
          const slideIndex = Math.round((scrollTop + windowHeight * 0.1) / windowHeight);
          const clampedSlide = Math.max(0, Math.min(slideIndex, totalSlides - 1));

          // ‚ú® DEBUGGING: Log para verificar c√°lculos
          console.log('üè† Home Scroll Debug:', {
            scrollTop,
            windowHeight,
            calculatedSlide: slideIndex,
            clampedSlide,
            currentSlide
          });

          if (clampedSlide !== currentSlide) {
            setCurrentSlide(clampedSlide);
            console.log('üè† Slide changed to:', clampedSlide);
          }

          ticking = false;
        });
        ticking = true;
      }
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, [currentSlide, totalSlides]);

  // ‚ú® MEJORAR: Funci√≥n para navegar a slide espec√≠fico
  const goToSlide = (slideIndex) => {
    const targetScroll = slideIndex * window.innerHeight;
    console.log('üè† Navigating to slide:', slideIndex, 'target scroll:', targetScroll);

    window.scrollTo({
      top: targetScroll,
      behavior: 'smooth'
    });

    // ‚ú® OPCIONAL: Actualizar estado inmediatamente para UI m√°s responsiva
    setCurrentSlide(slideIndex);
  };

  // Function to copy command to clipboard
  const copyCommand = async () => {
    try {
      await navigator.clipboard.writeText('ext install aMonteSl.code-xr');
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
    }
  };

  return (
    <div className="App" ref={containerRef}>
      <motion.div 
        className="progress-bar"
        style={{ scaleX }}
      />

      {/* Header */}
      <motion.header className="header">
        <div className="nav-container">
          <motion.div 
            className="logo-container"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <img src={CodeXRIconPNG} alt="Code-XR" className="logo-icon" />
            <span className="logo-text">Code-XR</span>
            {/* ‚ú® AGREGAR: Badge de versi√≥n en tiempo real */}
            <span className="version-badge">
