import { useState, useEffect } from 'react';
import { getExtensionStats, debugAPIFlags } from '../utils/marketplaceAPI';

export const useExtensionStats = () => {
  const [stats, setStats] = useState({
    downloads: null,
    installs: null,
    rating: null,
    ratingCount: null,
    version: null,
    loading: true,
    error: null
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        console.log('ðŸš€ Iniciando fetch con API v7.0 optimizada...');
        setStats(prev => ({ ...prev, loading: true, error: null }));

        // DEBUG: Solo en desarrollo, probar diferentes flags
        if (process.env.NODE_ENV === 'development' && window.location.search.includes('debug=flags')) {
          console.log('ðŸ§ª Modo DEBUG activado - Probando diferentes flags...');
          await debugAPIFlags();
        }

        const data = await getExtensionStats();

        if (data && !data.error) {
          console.log('âœ… Datos recibidos del mÃ©todo v7.0:', data);
          console.log('ðŸ·ï¸ VersiÃ³n recibida en hook v7.0:', data.version, '(tipo:', typeof data.version, ')');
          console.log('ðŸ“Š Debug info:', data.debug);

          const newStats = {
            downloads: (data.downloads && data.downloads > 0) ? data.downloads : null,
            installs: (data.installs && data.installs > 0) ? data.installs : null,
            rating: (data.rating && data.rating > 0) ? Math.round(data.rating * 10) / 10 : null,
            ratingCount: (data.ratingCount && data.ratingCount > 0) ? data.ratingCount : null,
            version: data.version || null,
            loading: false,
            error: null,
            debug: data.debug
          };

          console.log('ðŸ“ˆ Stats finales aplicadas v7.0:', newStats);
          console.log('ðŸ” ComparaciÃ³n detallada v7.0:');
          console.log(`  Downloads: ${stats.downloads} â†’ ${newStats.downloads} ${newStats.downloads ? 'âœ…' : 'âŒ'}`);
          console.log(`  Installs: ${stats.installs} â†’ ${newStats.installs} ${newStats.installs ? 'âœ…' : 'âŒ'}`);
          console.log(`  Rating: ${stats.rating} â†’ ${newStats.rating} ${newStats.rating ? 'âœ…' : 'âŒ'}`);
          console.log(`  Rating Count: ${stats.ratingCount} â†’ ${newStats.ratingCount} ${newStats.ratingCount ? 'âœ…' : 'âŒ'}`);
          console.log(`  Version: "${stats.version}" â†’ "${newStats.version}" ${newStats.version ? 'âœ…' : 'âŒ'}`);

          setStats(newStats);
        } else {
          console.warn('âš ï¸ Error en datos v7.0 o sin datos, manteniendo valores null');
          setStats(prev => ({ 
            ...prev, 
            loading: false,
            error: data?.error || 'No data received from v7.0 API'
          }));
        }
      } catch (error) {
        console.error('ðŸ’¥ Error en hook v7.0:', error);
        setStats(prev => ({
          ...prev,
          loading: false,
          error: 'Failed to load stats from v7.0 API'
        }));
      }
    };

    fetchStats();

    // Actualizar cada 5 minutos (mÃ¡s frecuente para testing)
    const interval = setInterval(fetchStats, 5 * 60 * 1000);

    return () => clearInterval(interval);
  }, []);

  return stats;
};
