import { useState, useEffect } from 'react';
import { getExtensionStats } from '../utils/marketplaceAPI';

export const useExtensionStats = () => {
  const [stats, setStats] = useState({
    downloads: 120, // Valores fallback
    installs: 38,
    rating: 4.8,
    ratingCount: 42,
    version: '2.0.0',
    loading: true,
    error: null
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        console.log('ðŸš€ Iniciando fetch con tu mÃ©todo exacto...');
        setStats(prev => ({ ...prev, loading: true, error: null }));

        const data = await getExtensionStats();

        if (data && !data.error) {
          console.log('âœ… Datos recibidos del mÃ©todo exacto:', data);

          const newStats = {
            // Usar los datos recibidos, mantener fallback solo si son 0 o undefined
            downloads: (data.downloads && data.downloads > 0) ? data.downloads : stats.downloads,
            installs: (data.installs && data.installs > 0) ? data.installs : stats.installs,
            rating: (data.rating && data.rating > 0) ? Math.round(data.rating * 10) / 10 : stats.rating,
            ratingCount: (data.ratingCount && data.ratingCount > 0) ? data.ratingCount : stats.ratingCount,
            version: data.version || stats.version,
            loading: false,
            error: null
          };

          console.log('ðŸ“ˆ Stats finales aplicadas:', newStats);
          console.log('ðŸ” ComparaciÃ³n:');
          console.log(`  Downloads: ${stats.downloads} â†’ ${newStats.downloads}`);
          console.log(`  Installs: ${stats.installs} â†’ ${newStats.installs}`);
          console.log(`  Rating: ${stats.rating} â†’ ${newStats.rating}`);

          setStats(newStats);
        } else {
          console.warn('âš ï¸ Error en datos o sin datos, manteniendo fallbacks');
          setStats(prev => ({ 
            ...prev, 
            loading: false,
            error: data?.error || 'No data received'
          }));
        }
      } catch (error) {
        console.error('ðŸ’¥ Error en hook:', error);
        setStats(prev => ({
          ...prev,
          loading: false,
          error: 'Failed to load stats'
        }));
      }
    };

    fetchStats();

    // Actualizar cada 10 minutos
    const interval = setInterval(fetchStats, 10 * 60 * 1000);

    return () => clearInterval(interval);
  }, []);

  return stats;
};
