import React, { useRef, useState, useEffect } from 'react';
import { motion, useScroll, useTransform, useSpring, AnimatePresence } from 'framer-motion';
import './App.css';

// Importar hooks y utilidades
import { useExtensionStats } from './hooks/useExtensionStats';
import { formatNumber, formatRating, formatVersion } from './utils/formatNumbers';

// Import technology logos
import NodejsLogo from './assets/logos/nodejs.svg';
import TypescriptLogo from './assets/logos/typescript.svg';
import AframeLogo from './assets/logos/aframe.png';
import BabiaXRLogo from './assets/logos/babiaxr.png';
import PythonLogo from './assets/logos/python.svg';
import WebXRLogo from './assets/logos/webxr.png';

// Import Code-XR logos
import CodeXRIcon from './assets/logos/icon.svg';       
import CodeXRIconPNG from './assets/logos/icon.png';    

function App() {
  const containerRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [copied, setCopied] = useState(false);
  const [currentTextIndex, setCurrentTextIndex] = useState(0);
  const [fabOpen, setFabOpen] = useState(false);
  const totalSlides = 6;

  // Hook para obtener estadísticas reales con tu método exacto
  const { downloads, installs, rating, ratingCount, version, loading, error } = useExtensionStats();

  // Rotating phrases for subtitle
  const rotatingTexts = [
    "Visualize your code metrics in Augmented and Virtual Reality.",
    "Transform static code analysis into immersive XR exploration.",
    "Experience your code in XR or on-screen — clear, structured, insightful.",
    "Step beyond the editor — analyze code with spatial context in XR.",
    "From static views to immersive XR — see what your code is really doing.",
    "Code metrics reimagined — interactively, spatially, in XR."
  ];

  // Main scroll hook for the entire page
  const { scrollYProgress } = useScroll({
    target: containerRef,
    offset: ["start start", "end end"]
  });

  // Progress bar for scroll
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  // Effect to change text every 6 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTextIndex((prevIndex) => 
        prevIndex === rotatingTexts.length - 1 ? 0 : prevIndex + 1
      );
    }, 6000);

    return () => clearInterval(interval);
  }, [rotatingTexts.length]);

  // Detect current slide based on scroll
  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll('.slide');
      const scrollPosition = window.scrollY + window.innerHeight * 0.3;

      sections.forEach((section, index) => {
        const sectionTop = section.offsetTop;
        const sectionHeight = section.offsetHeight;

        if (scrollPosition >= sectionTop && scrollPosition < sectionTop + sectionHeight) {
          setCurrentSlide(index);
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Function to navigate to specific slide
  const goToSlide = (slideIndex) => {
    const slides = document.querySelectorAll('.slide');
    if (slides[slideIndex]) {
      slides[slideIndex].scrollIntoView({ 
        behavior: 'smooth',
        block: 'start'
      });
    }
  };

  // Function to copy command to clipboard
  const copyCommand = async () => {
    try {
      await navigator.clipboard.writeText('ext install aMonteSl.code-xr');
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy text: ', err);
    }
  };

  // Close FAB when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (fabOpen && !event.target.closest('.fab-container')) {
        setFabOpen(false);
      }
    };

    if (fabOpen) {
      document.addEventListener('click', handleClickOutside);
    }

    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, [fabOpen]);

  return (
    <div className="App" ref={containerRef}>
      {/* Progress Bar */}
      <motion.div 
        className="progress-bar" 
        style={{ scaleX }}
      />

      {/* Header CON BADGE DE VERSIÓN */}
      <motion.header className="header">
        <div className="nav-container">
          <motion.div 
            className="logo-container"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => goToSlide(0)}
          >
            <img src={CodeXRIcon} alt="Code-XR" className="logo-icon" />
            <span className="logo-text">Code-XR</span>
            {/* ✨ NUEVO: BADGE DE VERSIÓN EN EL HEADER */}
            {version && !loading && (
              <motion.span 
                className="version-badge"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.3, delay: 0.5 }}
              >
                {formatVersion(version)}
              </motion.span>
            )}
          </motion.div>

          <nav className="nav-menu">
            <motion.button 
              onClick={() => goToSlide(0)}
              className="nav-link internal"
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              Home
            </motion.button>
            <motion.button 
              onClick={() => goToSlide(1)}
              className="nav-link internal"
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              Features
            </motion.button>
            <motion.button 
              onClick={() => goToSlide(2)}
              className="nav-link internal"
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              How to Use
            </motion.button>
            <motion.button 
              onClick={() => goToSlide(3)}
              className="nav-link internal"
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              Benefits
            </motion.button>
            <motion.button 
              onClick={() => goToSlide(4)}
              className="nav-link internal"
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              Download
            </motion.button>
            <motion.button 
              onClick={() => goToSlide(5)}
              className="nav-link internal"
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              Technologies
            </motion.button>

            <div className="nav-separator"></div>

            <motion.a
              href="https://marketplace.visualstudio.com/items?itemName=aMonteSl.code-xr"
              target="_blank"
              rel="noopener noreferrer"
              className="nav-link external"
              whileHover={{ 
                scale: 1.1, 
                color: "#ff6b6b",
                textShadow: "0 0 8px rgba(255, 107, 107, 0.4)"
              }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              <span className="nav-text">Marketplace</span>
              <span className="external-icon">↗</span>
            </motion.a>

            <motion.a
              href="https://github.com/aMonteSl/CodeXR"
              target="_blank"
              rel="noopener noreferrer"
              className="nav-link external"
              whileHover={{ 
                scale: 1.1, 
                color: "#feca57",
                textShadow: "0 0 8px rgba(254, 202, 87, 0.4)"
              }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              <span className="nav-text">GitHub</span>
              <span className="external-icon">↗</span>
            </motion.a>
          </nav>
        </div>
      </motion.header>

      {/* Hero Slide */}
      <motion.section 
        className="slide hero-slide"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <motion.div 
          className="hero-content"
          initial={{ y: 100, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 1, delay: 0.3 }}
        >
          <motion.div 
            className="hero-logo-container"
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <img src={CodeXRIcon} alt="Code-XR Logo" className="hero-logo" />
          </motion.div>

          <motion.h1 
            className="hero-title"
            initial={{ scale: 0.8 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.8, delay: 0.5 }}
          >
            <span className="brand">Code-XR</span> for VS Code
          </motion.h1>

          <div className="hero-subtitle-container">
            <AnimatePresence mode="wait">
              <motion.p 
                key={currentTextIndex}
                className="hero-subtitle"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ 
                  duration: 0.8,
                  ease: "easeInOut"
                }}
              >
                {rotatingTexts[currentTextIndex]}
              </motion.p>
            </AnimatePresence>
          </div>

          <motion.div
            className="install-command"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.75 }}
            onClick={copyCommand}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <span className="command-label">Quick Install:</span>
            <code className="command-text">ext install aMonteSl.code-xr</code>
            <motion.button 
              className="copy-button"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              animate={copied ? { scale: [1, 1.2, 1] } : {}}
              transition={{ duration: 0.3 }}
            >
              {copied ? '✓' : '📋'}
            </motion.button>

            <AnimatePresence>
              {copied && (
                <motion.div
                  className="copy-tooltip"
                  initial={{ opacity: 0, y: 10, scale: 0.8 }}
                  animate={{ opacity: 1, y: 0, scale: 1 }}
                  exit={{ opacity: 0, y: -10, scale: 0.8 }}
                  transition={{ duration: 0.2 }}
                >
                  Copied!
                </motion.div>
              )}
            </AnimatePresence>
          </motion.div>

          <motion.div
            className="hero-badges"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            <span className="badge">🥽 VR/AR</span>
            <span className="badge">📊 Metrics</span>
            <span className="badge">⚡ Real-time</span>
          </motion.div>

          {/* STATS DINÁMICAS CON TU MÉTODO */}
          {(loading || downloads !== null || installs !== null || rating !== null) && (
            <motion.div
              className="hero-stats"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.85 }}
            >
              <div className={`stats-container ${loading ? 'loading' : ''}`}>
                <motion.div 
                  className="stat-item"
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  transition={{ duration: 0.6, delay: 0.9 }}
                >
                  <div className={`stat-number ${loading ? 'loading' : ''}`}>
                    {loading ? '...' : formatNumber(downloads)}
                  </div>
                  <div className="stat-label">Total Downloads</div>
                </motion.div>

                <div className="stats-separator"></div>

                <motion.div 
                  className="stat-item"
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  transition={{ duration: 0.6, delay: 1.0 }}
                >
                  <div className={`stat-number ${loading ? 'loading' : ''}`}>
                    {loading ? '...' : formatNumber(installs)}
                  </div>
                  <div className="stat-label">Active Installs</div>
                </motion.div>

                <div className="stats-separator"></div>

                <motion.div 
                  className="stat-item"
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  transition={{ duration: 0.6, delay: 1.1 }}
                >
                  <div className={`stat-number ${loading ? 'loading' : ''}`}>
                    {loading ? '...' : formatRating(rating)}
                  </div>
                  <div className="stat-label">User Rating</div>
                </motion.div>
              </div>

              {error && (
                <div className="stats-error">
                  Unable to load current statistics
                </div>
              )}
            </motion.div>
          )}

          <motion.button 
            className="cta-button"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 1.2 }} // Aumentamos delay por las stats
            whileHover={{ 
              scale: 1.05, 
              boxShadow: "0px 10px 30px rgba(0,0,0,0.3)",
              background: "linear-gradient(45deg, #ff8a8a, #5ed9d1)"
            }}
            whileTap={{ scale: 0.95 }}
            onClick={() => goToSlide(1)}
          >
            Explore Features
          </motion.button>
        </motion.div>

        <motion.div 
          className="next-section-arrow"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 1.2 }}
          onClick={() => goToSlide(1)}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          <span className="arrow-text">Key Features</span>
          <motion.div 
            className="arrow-icon"
            animate={{ y: [0, 8, 0] }}
            transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
          >
            ↓
          </motion.div>
        </motion.div>
      </motion.section>

      {/* Features Slide */}
      <motion.section 
        className="slide features-slide"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <motion.h2 
          className="section-title"
          initial={{ y: 50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
