import React, { useRef, useState, useEffect } from 'react';
import { motion, useScroll, useSpring, AnimatePresence } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import '../App.css';

// Importar hooks y utilidades
import { useExtensionStats } from '../hooks/useExtensionStats';
import { formatNumber, formatRating, formatVersion } from '../utils/formatNumbers';

// Import technology logos
import NodejsLogo from '../assets/logos/nodejs.svg';
import TypescriptLogo from '../assets/logos/typescript.svg';
import AframeLogo from '../assets/logos/aframe.png';
import BabiaXRLogo from '../assets/logos/babiaxr.png';
import PythonLogo from '../assets/logos/python.svg';
import WebXRLogo from '../assets/logos/webxr.png';

// Import Code-XR logos
import CodeXRIcon from '../assets/logos/icon.svg';  // ✨ MANTENER SVG para el hero     
import CodeXRIconPNG from '../assets/logos/icon.png'; // ✨ USAR PNG para el header

// ✨ NUEVO: Importar componente FAB
import { FAB } from '../components';

function Home() {
  const navigate = useNavigate();
  const containerRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [copied, setCopied] = useState(false);
  const [currentTextIndex, setCurrentTextIndex] = useState(0);
  // ✨ REMOVER: const [fabOpen, setFabOpen] = useState(false);
  const totalSlides = 6;

  // Hook para obtener estadísticas reales
  const { downloads, installs, rating, version, loading, error } = useExtensionStats();

  // ✨ DEBUGGING: Log de versión en tiempo real
  useEffect(() => {
    console.log('🏠 Home Component - Version State:', {
      version,
      loading,
      error,
      formattedVersion: formatVersion(version)
    });
  }, [version, loading, error]);

  // Rotating phrases for subtitle
  const rotatingTexts = [
    "Visualize your code metrics in Augmented and Virtual Reality.",
    "Transform static code analysis into immersive XR exploration.",
    "Experience your code in XR or on-screen — clear, structured, insightful.",
    "Step beyond the editor — analyze code with spatial context in XR.",
    "From static views to immersive XR — see what your code is really doing.",
    "Code metrics reimagined — interactively, spatially, in XR."
  ];

  // Main scroll hook for the entire page
  const { scrollYProgress } = useScroll({
    target: containerRef,
    offset: ["start start", "end end"]
  });

  // Progress bar for scroll
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  // Effect to change text every 6 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTextIndex((prev) => (prev + 1) % rotatingTexts.length);
    }, 6000);

    return () => clearInterval(interval);
  }, [rotatingTexts.length]);

  // Detect current slide based on scroll
  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.scrollY;
      const windowHeight = window.innerHeight;

      // ✨ SOLUCIÓN: Aplicar offset para compensar el botón al final de Features
      const offset = windowHeight * 0.15; // 15% del viewport como buffer
      const adjustedScrollTop = scrollTop + offset;

      const newSlide = Math.round(adjustedScrollTop / windowHeight);
      setCurrentSlide(Math.min(newSlide, totalSlides - 1));
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Function to navigate to specific slide
  const goToSlide = (slideIndex) => {
    const targetScroll = slideIndex * window.innerHeight;
    window.scrollTo({
      top: targetScroll,
      behavior: 'smooth'
    });
  };

  // Function to copy command to clipboard
  const copyCommand = async () => {
    try {
      await navigator.clipboard.writeText('ext install aMonteSl.code-xr');
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy command:', err);
    }
  };

  // ✨ REMOVER: Close FAB when clicking outside (movido al componente FAB)

  return (
    <div className="App" ref={containerRef}>
      {/* Progress Bar */}
      <motion.div 
        className="progress-bar" 
        style={{ scaleX }}
      />

      {/* Header */}
      <motion.header className="header">
        <div className="nav-container">
          <motion.div 
            className="logo-container"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => goToSlide(0)}
          >
            <img src={CodeXRIconPNG} alt="Code-XR" className="logo-icon" />
            <span className="logo-text">Code-XR</span>
            {version && !loading && (
              <motion.span 
                className="version-badge"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.3, delay: 0.5 }}
                title={`Versión actual: ${version}`}
              >
                {formatVersion(version)}
              </motion.span>
            )}
            {loading && (
              <motion.span 
                className="version-badge loading"
                initial={{ opacity: 0 }}
                animate={{ opacity: 0.7 }}
              >
                ...
              </motion.span>
            )}
          </motion.div>

          <nav className="nav-menu">
            <button className="nav-link internal" onClick={() => goToSlide(0)}>
              Home
            </button>
            <button className="nav-link internal" onClick={() => goToSlide(1)}>
              Features
            </button>
            <button className="nav-link internal" onClick={() => goToSlide(2)}>
              How to Use
            </button>
            <button className="nav-link internal" onClick={() => goToSlide(3)}>
              Benefits
            </button>
            <button className="nav-link internal" onClick={() => goToSlide(4)}>
              Download
            </button>
            <button className="nav-link internal" onClick={() => goToSlide(5)}>
              Technologies
            </button>
            <div className="nav-separator"></div>
            <a 
              href="https://marketplace.visualstudio.com/items?itemName=aMonteSl.code-xr" 
              className="nav-link external"
              target="_blank"
              rel="noopener noreferrer"
            >
              <span className="nav-text">Marketplace</span>
              <span className="external-icon">↗</span>
            </a>
            <a 
              href="https://github.com/aMonteSl/babiaxr-vscode" 
              className="nav-link external"
              target="_blank"
              rel="noopener noreferrer"
            >
              <span className="nav-text">GitHub</span>
              <span className="external-icon">↗</span>
            </a>
          </nav>
        </div>
      </motion.header>

      {/* Hero Slide */}
      <motion.section 
        className="slide hero-slide"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <motion.div 
          className="hero-content"
          initial={{ y: 100, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 1, delay: 0.3 }}
        >
          <motion.div className="hero-logo-container">
            {/* ✨ MANTENER: SVG para el hero (se ve mejor con filtros) */}
            <img src={CodeXRIcon} alt="Code-XR Logo" className="hero-logo" />
          </motion.div>

          <motion.h1 
            className="hero-title"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.5 }}
          >
            <span className="brand">Code</span>-XR
          </motion.h1>

          <div className="hero-subtitle-container">
            <AnimatePresence mode="wait">
              <motion.p
                key={currentTextIndex}
                className="hero-subtitle"
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -10 }}
                transition={{ duration: 0.5 }}
              >
                {rotatingTexts[currentTextIndex]}
              </motion.p>
            </AnimatePresence>
          </div>

          <motion.div
            className="hero-badges"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.7 }}
          >
            <span className="badge">VS Code Extension</span>
            <span className="badge">Code Metrics</span>
            <span className="badge">Desktop/XR</span>
            <span className="badge">Open Source</span>
          </motion.div>

          {/* STATS DINÁMICAS */}
          {(loading || downloads !== null || installs !== null || rating !== null || version !== null) && (
            <motion.div
              className="hero-stats"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.85 }}
            >
              <div className={`stats-container ${loading ? 'loading' : ''}`}>
                <motion.div 
                  className="stat-item"
                  data-type="downloads"
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  transition={{ duration: 0.6, delay: 0.9 }}
                >
                  <div className={`stat-number ${loading ? 'loading' : ''}`}>
                    {loading ? '...' : formatNumber(downloads)}
                  </div>
                  <div className="stat-label">Total Downloads</div>
                </motion.div>

                <div className="stats-separator"></div>

                <motion.div 
                  className="stat-item"
                  data-type="installs"
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  transition={{ duration: 0.6, delay: 1.0 }}
                >
                  <div className={`stat-number ${loading ? 'loading' : ''}`}>
                    {loading ? '...' : formatNumber(installs)}
                  </div>
                  <div className="stat-label">Active Installs</div>
                </motion.div>

                <div className="stats-separator"></div>

                <motion.div 
                  className="stat-item"
                  data-type="rating"
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  transition={{ duration: 0.6, delay: 1.1 }}
                >
                  <div className={`stat-number ${loading ? 'loading' : ''}`}>
                    {loading ? '...' : formatRating(rating)}
                  </div>
                  <div className="stat-label">User Rating</div>
                </motion.div>

                <div className="stats-separator"></div>

                <motion.div 
                  className="stat-item"
                  data-type="version"
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  transition={{ duration: 0.6, delay: 1.2 }}
                >
                  <div className={`stat-number version-number ${loading ? 'loading' : ''}`}>
                    {loading ? '...' : formatVersion(version)}
                  </div>
                  <div className="stat-label">Version</div>
                </motion.div>
              </div>

              {error && (
                <div className="stats-error">
                  Unable to load current statistics
                </div>
              )}
            </motion.div>
          )}

          <motion.div 
            className="next-section-arrow"
            onClick={() => goToSlide(1)}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.8, delay: 1.3 }}
          >
            <span className="arrow-icon">↓</span>
          </motion.div>
        </motion.div>
      </motion.section>

      {/* Features Slide */}
      <motion.section 
        className="slide features-slide"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <motion.h2 
          className="section-title"
          initial={{ y: 50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          <span className="brand">Key</span> Features
        </motion.h2>

        <motion.div 
          className="features-grid"
          initial={{ y: 100, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 1, delay: 0.4 }}
        >
          {/* ... 6 feature cards igual ... */}
          <motion.div 
            className="feature-card"
            style={{ '--accent-color': '#ff6b6b' }}
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.5 }}
          >
            <div className="feature-icon">🔍</div>
            <h3>Interactive Visualization</h3>
            <p>Explore your code in an immersive 3D environment where every metric comes alive with dynamic visualizations and intuitive navigation.</p>
          </motion.div>

          <motion.div 
            className="feature-card"
            style={{ '--accent-color': '#4ecdc4' }}
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            <div className="feature-icon">📊</div>
            <h3>Real-time Metrics</h3>
            <p>Instant analysis of complexity, code quality, and design patterns presented in a visual and comprehensible format.</p>
          </motion.div>

          <motion.div 
            className="feature-card"
            style={{ '--accent-color': '#45b7d1' }}
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.7 }}
          >
            <div className="feature-icon">🥽</div>
            <h3>Complete XR Experience</h3>
            <p>Compatible with AR/VR devices for total immersion or on-screen visualization for maximum accessibility.</p>
          </motion.div>

          <motion.div 
            className="feature-card"
            style={{ '--accent-color': '#96ceb4' }}
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            <div className="feature-icon">⚡</div>
            <h3>Seamless Integration</h3>
            <p>Integrates directly into VS Code without additional configuration. Activate XR with a single click from your favorite editor.</p>
          </motion.div>

          <motion.div 
            className="feature-card"
            style={{ '--accent-color': '#feca57' }}
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.9 }}
          >
            <div className="feature-icon">🎯</div>
            <h3>Smart Analysis</h3>
            <p>Advanced algorithms that identify patterns, suggest improvements, and highlight critical areas of your codebase.</p>
          </motion.div>

          <motion.div 
            className="feature-card"
            style={{ '--accent-color': '#ff9ff3' }}
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.8, delay: 1.0 }}
          >
            <div className="feature-icon">🔄</div>
            <h3>Enhanced Collaboration</h3>
            <p>Share XR visualizations with your team for more effective code reviews and shared understanding.</p>
          </motion.div>
        </motion.div>

        {/* ✨ MEJORADO: Botón Discover More con mejor espaciado */}
        <motion.div
          className="features-cta-improved"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          viewport={{ once: true }}
        >
          <motion.button
            className="discover-more-button-enhanced"
            onClick={() => {
              // ✨ NAVEGACIÓN LIMPIA: Resetear scroll antes de navegar
              window.scrollTo(0, 0);
              navigate('/key-features');
            }}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <span>Discover More Features</span>
            <span className="discover-icon-enhanced">→</span>
          </motion.button>
          <p className="features-cta-description">
            Explore detailed capabilities and technical specifications
          </p>
        </motion.div>

        {/* ✨ Flecha con espaciado adicional */}
        <motion.div 
          className="next-section-arrow next-section-arrow-spaced"
          onClick={() => goToSlide(2)}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5, delay: 1 }}
        >
          <motion.div 
            className="arrow-icon"
            animate={{ y: [0, 8, 0] }}
            transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
          >
            ↓
          </motion.div>
        </motion.div>
      </motion.section>

      {/* How to Use Slide */}
      <motion.section 
        className="slide howto-slide"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true, amount: 0.5 }}
      >
        <motion.h2 
          className="section-title"
          initial={{ y: 50, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          <span className="brand">How</span> to Use Code-XR
        </motion.h2>

        <motion.div
          className="steps-container"
          initial={{ y: 30, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          viewport={{ once: true }}
        >
          {[
            {
              step: "01",
              title: "XR Analysis",
              desc: "Right-click on a file and select 'CodeXR Analyze File: XR'",
              icon: "🖱️"
            },
            {
              step: "02", 
              title: "3D Visualization",
              desc: "A browser window opens with the 3D visualization of your code",
              icon: "🌐"
            },
            {
              step: "03",
              title: "Navigation Modes",
              desc: "AR: Real world • VR: Full immersion • 3D: Mouse controls",
              icon: "🎮"
            }
          ].map((step, index) => (
            <motion.div 
              key={index}
              className="step-item"
              initial={{ opacity: 0, x: -50 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: 0.4 + index * 0.2 }}
              viewport={{ once: true }}
              whileHover={{ scale: 1.02 }}
            >
              <div className="step-number">{step.step}</div>
              <div className="step-content">
                <div className="step-icon">{step.icon}</div>
                <h3>{step.title}</h3>
                <p>{step.desc}</p>
              </div>
            </motion.div>
          ))}
        </motion.div>

        <motion.div 
          className="next-section-arrow"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.8 }}
          viewport={{ once: true }}
          onClick={() => goToSlide(3)}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          <motion.div 
            className="arrow-icon"
            animate={{ y: [0, 8, 0] }}
            transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
          >
            ↓
          </motion.div>
        </motion.div>
      </motion.section>

      {/* Benefits Slide */}
      <motion.section 
        className="slide benefits-slide"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true, amount: 0.5 }}
