import React, { useRef, useState, useEffect } from 'react';
import { motion, useScroll, useTransform, useSpring, AnimatePresence } from 'framer-motion';
import './App.css';

// Importar hooks y utilidades
import { useExtensionStats } from './hooks/useExtensionStats';
import { formatNumber, formatRating, formatVersion } from './utils/formatNumbers';

// Import technology logos
import NodejsLogo from './assets/logos/nodejs.svg';
import TypescriptLogo from './assets/logos/typescript.svg';
import AframeLogo from './assets/logos/aframe.png';
import BabiaXRLogo from './assets/logos/babiaxr.png';
import PythonLogo from './assets/logos/python.svg';
import WebXRLogo from './assets/logos/webxr.png';

// Import Code-XR logos
import CodeXRIcon from './assets/logos/icon.svg';       
import CodeXRIconPNG from './assets/logos/icon.png';    

function App() {
  const containerRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [copied, setCopied] = useState(false);
  const [currentTextIndex, setCurrentTextIndex] = useState(0);
  const [fabOpen, setFabOpen] = useState(false);
  const totalSlides = 6;

  // Hook para obtener estadísticas reales con tu método exacto
  const { downloads, installs, rating, ratingCount, version, loading, error } = useExtensionStats();

  // Rotating phrases for subtitle
  const rotatingTexts = [
    "Visualize your code metrics in Augmented and Virtual Reality.",
    "Transform static code analysis into immersive XR exploration.",
    "Experience your code in XR or on-screen — clear, structured, insightful.",
    "Step beyond the editor — analyze code with spatial context in XR.",
    "From static views to immersive XR — see what your code is really doing.",
    "Code metrics reimagined — interactively, spatially, in XR."
  ];

  // Main scroll hook for the entire page
  const { scrollYProgress } = useScroll({
    target: containerRef,
    offset: ["start start", "end end"]
  });

  // Progress bar for scroll
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  // Effect to change text every 6 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTextIndex((prevIndex) => 
        prevIndex === rotatingTexts.length - 1 ? 0 : prevIndex + 1
      );
    }, 6000);

    return () => clearInterval(interval);
  }, [rotatingTexts.length]);

  // Detect current slide based on scroll
  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const windowHeight = window.innerHeight;
      const newSlide = Math.floor(scrollPosition / windowHeight);
      setCurrentSlide(Math.min(newSlide, totalSlides - 1));
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Function to navigate to specific slide
  const goToSlide = (slideIndex) => {
    const targetY = slideIndex * window.innerHeight;
    window.scrollTo({
      top: targetY,
      behavior: 'smooth'
    });
  };

  // Function to copy command to clipboard
  const copyCommand = async () => {
    try {
      await navigator.clipboard.writeText('ext install aMonteSl.code-xr');
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy: ', err);
    }
  };

  // Close FAB when clicking outside
  useEffect(() => {
    const handleClick = (e) => {
      if (!e.target.closest('.fab-container')) {
        setFabOpen(false);
      }
    };

    if (fabOpen) {
      document.addEventListener('click', handleClick);
      return () => document.removeEventListener('click', handleClick);
    }
  }, [fabOpen]);

  return (
    <div className="App" ref={containerRef}>
      {/* Progress Bar */}
      <motion.div 
        className="progress-bar" 
        style={{ scaleX }}
      />

      {/* Header */}
      <motion.header className="header">
        <div className="nav-container">
          <motion.div 
            className="logo-container"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => goToSlide(0)}
          >
            <img src={CodeXRIcon} alt="Code-XR" className="logo-icon" />
            <span className="logo-text">Code-XR</span>
          </motion.div>

          <nav className="nav-menu">
            <button 
              className="nav-link internal" 
              onClick={() => goToSlide(1)}
            >
              Features
            </button>
            <button 
              className="nav-link internal" 
              onClick={() => goToSlide(2)}
            >
              How to Use
            </button>
            <button 
              className="nav-link internal" 
              onClick={() => goToSlide(3)}
            >
              Benefits
            </button>
            <button 
              className="nav-link internal" 
              onClick={() => goToSlide(4)}
            >
              Technologies
            </button>
            <button 
              className="nav-link internal" 
              onClick={() => goToSlide(5)}
            >
              Download
            </button>

            <div className="nav-separator"></div>

            <a 
              href="https://github.com/aMonteSl/babiaxr-vscode" 
              target="_blank" 
              rel="noopener noreferrer"
              className="nav-link external"
            >
              <span className="nav-text">GitHub</span>
              <span className="external-icon">↗</span>
            </a>
            <a 
              href="https://marketplace.visualstudio.com/items?itemName=aMonteSl.code-xr" 
              target="_blank" 
              rel="noopener noreferrer"
              className="nav-link external"
            >
              <span className="nav-text">Marketplace</span>
              <span className="external-icon">↗</span>
            </a>
          </nav>
        </div>
      </motion.header>

      {/* Hero Slide */}
      <motion.section 
        className="slide hero-slide"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <motion.div 
          className="hero-content"
          initial={{ y: 100, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 1, delay: 0.3 }}
        >
          <motion.div 
            className="hero-logo-container"
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.1 }}
          >
            <img src={CodeXRIcon} alt="Code-XR Logo" className="hero-logo" />
          </motion.div>

          <motion.h1 
            className="hero-title"
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <span className="brand">Code</span>-XR
          </motion.h1>

          <div className="hero-subtitle-container">
            <AnimatePresence mode="wait">
              <motion.p
                key={currentTextIndex}
                className="hero-subtitle"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.6 }}
              >
                {rotatingTexts[currentTextIndex]}
              </motion.p>
            </AnimatePresence>
          </div>

          <motion.div
            className="hero-badges"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.7 }}
          >
            <span className="badge">VS Code Extension</span>
            <span className="badge">AR/VR Ready</span>
            <span className="badge">Real-time Metrics</span>
          </motion.div>

          {/* STATS DINÁMICAS CON VERSIÓN - Ahora con 4 elementos */}
          {(loading || downloads !== null || installs !== null || rating !== null || version !== null) && (
            <motion.div
              className="hero-stats"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.85 }}
            >
              <div className={`stats-container ${loading ? 'loading' : ''}`}>
                <motion.div 
                  className="stat-item"
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  transition={{ duration: 0.6, delay: 0.9 }}
                >
                  <div className={`stat-number ${loading ? 'loading' : ''}`}>
                    {loading ? '...' : formatNumber(downloads)}
                  </div>
                  <div className="stat-label">Total Downloads</div>
                </motion.div>

                <div className="stats-separator"></div>

                <motion.div 
                  className="stat-item"
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  transition={{ duration: 0.6, delay: 1.0 }}
                >
                  <div className={`stat-number ${loading ? 'loading' : ''}`}>
                    {loading ? '...' : formatNumber(installs)}
                  </div>
                  <div className="stat-label">Active Installs</div>
                </motion.div>

                <div className="stats-separator"></div>

                <motion.div 
                  className="stat-item"
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  transition={{ duration: 0.6, delay: 1.1 }}
                >
                  <div className={`stat-number ${loading ? 'loading' : ''}`}>
                    {loading ? '...' : formatRating(rating)}
                  </div>
                  <div className="stat-label">User Rating</div>
                </motion.div>

                <div className="stats-separator"></div>

                {/* NUEVA STAT: VERSIÓN */}
                <motion.div 
                  className="stat-item"
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  transition={{ duration: 0.6, delay: 1.2 }}
                >
                  <div className={`stat-number version-number ${loading ? 'loading' : ''}`}>
                    {loading ? '...' : formatVersion(version)}
                  </div>
                  <div className="stat-label">Latest Version</div>
                </motion.div>
              </div>

              {error && (
                <div className="stats-error">
                  Unable to load current statistics
                </div>
              )}
            </motion.div>
          )}

          <motion.div 
            className="next-section-arrow"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1, delay: 1.5 }}
            onClick={() => goToSlide(1)}
          >
            <div className="arrow-icon">↓</div>
          </motion.div>
        </motion.div>
      </motion.section>

      {/* Features Slide */}
      <motion.section 
        className="slide features-slide"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
      >
        <motion.h2 
          className="section-title"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <span className="brand">Features</span> & Capabilities
        </motion.h2>

        <div className="features-grid">
          {[
            {
              icon: "🥽",
              title: "VR/AR Visualization",
              description: "Immerse yourself in your codebase with cutting-edge VR and AR technology. Navigate through your project structure in three-dimensional space.",
              color: "#ff6b6b"
            },
            {
              icon: "📊",
              title: "Real-time Metrics",
              description: "Get instant insights into code complexity, coverage, and performance metrics. Watch your code quality evolve in real-time as you develop.",
              color: "#4ecdc4"
            },
            {
              icon: "🎯",
              title: "Interactive Navigation",
              description: "Point, click, and teleport through your code. Use hand gestures and voice commands to navigate your project like never before.",
              color: "#45b7d1"
            },
            {
              icon: "🔍",
              title: "Code Analysis",
              description: "Deep dive into code patterns, dependencies, and architectural insights. Identify bottlenecks and optimization opportunities instantly.",
              color: "#96ceb4"
            },
            {
              icon: "⚡",
              title: "Performance Insights",
              description: "Monitor execution times, memory usage, and resource consumption. Optimize your code with visual performance indicators.",
              color: "#feca57"
            },
            {
              icon: "🎨",
              title: "Customizable Views",
              description: "Tailor the visualization to your needs. Create custom dashboards and save your preferred viewing configurations.",
              color: "#ff9ff3"
            }
          ].map((feature, index) => (
            <motion.div
              key={index}
              className="feature-card"
              style={{ '--accent-color': feature.color }}
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              viewport={{ once: true }}
            >
              <div className="feature-icon">
                {feature.icon}
              </div>
              <h3>{feature.title}</h3>
              <p>{feature.description}</p>
            </motion.div>
          ))}
        </div>

        <motion.div 
          className="next-section-arrow"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 0.6, delay: 0.8 }}
          viewport={{ once: true }}
          onClick={() => goToSlide(2)}
        >
          <div className="arrow-icon">↓</div>
        </motion.div>
      </motion.section>

      {/* How To Use Slide */}
      <motion.section 
        className="slide howto-slide"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
      >
        <motion.h2 
          className="section-title"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <span className="brand">How</span> to Use
        </motion.h2>

        <motion.div 
          className="steps-container"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          viewport={{ once: true }}
        >
          {[
            {
              number: "01",
              icon: "⬇️",
              title: "Install Extension",
              description: "Search for 'Code-XR' in VS Code marketplace or use the command palette to install directly."
            },
            {
              number: "02", 
              icon: "🔧",
              title: "Configure Settings",
              description: "Set up your preferred VR/AR device and customize visualization parameters in the extension settings."
            },
            {
              number: "03",
              icon: "🚀",
              title: "Launch XR Mode",
              description: "Open your project and activate XR mode using Ctrl+Shift+X or the command 'Code-XR: Start Session'."
            },
            {
              number: "04",
              icon: "🎮",
              title: "Explore & Analyze",
              description: "Navigate through your code in 3D space, interact with metrics, and gain new insights into your project structure."
            }
          ].map((step, index) => (
            <motion.div
              key={index}
              className="step-item"
              initial={{ opacity: 0, x: -50 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: index * 0.15 }}
              viewport={{ once: true }}
            >
              <div className="step-number">{step.number}</div>
              <div className="step-content">
                <span className="step-icon">{step.icon}</span>
                <h3>{step.title}</h3>
                <p>{step.description}</p>
              </div>
            </motion.div>
          ))}
        </motion.div>

        <motion.div 
          className="next-section-arrow"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 0.6, delay: 0.8 }}
          viewport={{ once: true }}
          onClick={() => goToSlide(3)}
        >
          <div className="arrow-icon">↓</div>
        </motion.div>
      </motion.section>

      {/* Benefits Slide */}
      <motion.section 
        className="slide benefits-slide"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
      >
        <motion.h2 
          className="section-title"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <span className="brand">Benefits</span> & Impact
        </motion.h2>

        <motion.div 
          className="benefits-grid"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          viewport={{ once: true }}
        >
          {[
            {
              icon: "⚡",
              title: "Faster Development",
              description: "Reduce debugging time by up to 40% with visual code insights and immersive problem identification.",
              stats: "-40%"
            },
            {
              icon: "🎯",
              title: "Better Code Quality",
              description: "Improve code maintainability and reduce technical debt with comprehensive visual analysis tools.",
              stats: "+25%"
            },
            {
              icon: "🧠",
              title: "Enhanced Understanding",
              description: "Grasp complex architectures faster with 3D visualization and spatial code organization.",
              stats: "3x"
            },
            {
              icon: "👥",
              title: "Team Collaboration",
              description: "Share immersive code reviews and conduct virtual pair programming sessions with your team.",
              stats: "+60%"
            }
          ].map((benefit, index) => (
            <motion.div
              key={index}
              className="benefit-card"
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: index * 0.15 }}
              viewport={{ once: true }}
            >
              <div className="benefit-stats">{benefit.stats}</div>
              <span className="benefit-icon">{benefit.icon}</span>
              <h3>{benefit.title}</h3>
              <p>{benefit.description}</p>
            </motion.div>
          ))}
        </motion.div>

        <motion.div 
          className="next-section-arrow"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 0.6, delay: 0.8 }}
          viewport={{ once: true }}
          onClick={() => goToSlide(4)}
        >
          <div className="arrow-icon">↓</div>
        </motion.div>
      </motion.section>

      {/* Technologies Slide */}
      <motion.section 
        className="slide technologies-slide"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
      >
        <motion.h2 
          className="section-title"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <span className="brand">Technologies</span> & Stack
        </motion.h2>

        <motion.p 
          className="technologies-subtitle"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          viewport={{ once: true }}
        >
          Built with modern web technologies and cutting-edge XR frameworks to deliver the best immersive development experience.
        </motion.p>

        <motion.div 
          className="technologies-grid"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.4 }}
          viewport={{ once: true }}
        >
          {[
            {
              name: "Node.js",
              description: "Backend runtime for extension logic and real-time data processing",
              logo: NodejsLogo,
              color: "#339933"
            },
            {
              name: "TypeScript", 
              description: "Type-safe development with enhanced code quality and maintainability",
              logo: TypescriptLogo,
              color: "#3178c6"
            },
            {
              name: "A-Frame",
              description: "Web framework for building virtual reality experiences directly in the browser",
              logo: AframeLogo,
              color: "#ef2d5e"
            },
            {
              name: "BabiaXR",
              description: "Data visualization framework optimized for immersive XR environments",
              logo: BabiaXRLogo,
              color: "#ff6b6b"
            },
            {
              name: "Python",
              description: "Data analysis and machine learning capabilities for advanced code insights",
              logo: PythonLogo,
              color: "#3776ab"
            },
            {
              name: "WebXR",
              description: "Cross-platform XR experiences that work across VR/AR devices and browsers",
              logo: WebXRLogo,
              color: "#4285f4"
            }
          ].map((tech, index) => (
            <motion.div
              key={index}
              className="tech-card"
              style={{ '--tech-color': tech.color }}
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              viewport={{ once: true }}
            >
              <div className="tech-logo-container">
                <img src={tech.logo} alt={`${tech.name} logo`} className="tech-logo" />
              </div>
              <h3 className="tech-name">{tech.name}</h3>
              <p className="tech-description">{tech.description}</p>
            </motion.div>
          ))}
        </motion.div>

        <motion.div 
          className="technologies-footer"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.8 }}
          viewport={{ once: true }}
        >
          <p>
            Continuously updated with the latest XR standards and development frameworks to ensure compatibility and performance.
          </p>
        </motion.div>

        <motion.div 
          className="next-section-arrow"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 0.6, delay: 1.0 }}
          viewport={{ once: true }}
          onClick={() => goToSlide(5)}
        >
          <div className="arrow-icon">↓</div>
        </motion.div>
      </motion.section>

      {/* Download Slide */}
      <motion.section 
        className="slide download-slide"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
      >
        <motion.div 
          className="download-content"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <motion.h2 
            className="section-title"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <span className="brand">Get</span> Started Now
          </motion.h2>

          <motion.p 
            className="download-text"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            viewport={{ once: true }}
          >
            Transform your development workflow with immersive code visualization. Install Code-XR and experience the future of software development.
          </motion.p>

          {/* Stats dinámicas en download también */}
          {(loading || downloads !== null || installs !== null || rating !== null) && (
            <motion.div
              className="download-stats"
              initial={{ opacity: 0, y: 15 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.3 }}
              viewport={{ once: true }}
            >
              <div className="download-stats-grid">
                <div className="download-stat">
                  <span className={`download-stat-number ${loading ? 'loading' : ''}`}>
                    {loading ? '...' : formatNumber(downloads)}
                  </span>
                  <span className="download-stat-label">Downloads</span>
                </div>
                <div className="download-stat">
                  <span className={`download-stat-number ${loading ? 'loading' : ''}`}>
                    {loading ? '...' : formatNumber(installs)}
                  </span>
                  <span className="download-stat-label">Active Users</span>
                </div>
                <div className="download-stat">
                  <span className={`download-stat-number ${loading ? 'loading' : ''}`}>
                    {loading ? '...' : formatRating(rating)}
                  </span>
                  <span className="download-stat-label">Rating</span>
                </div>
                <div className="download-stat">
                  <span className={`download-stat-number ${loading ? 'loading' : ''}`}>
                    {loading ? '...' : formatVersion(version)}
                  </span>
                  <span className="download-stat-label">Version</span>
                </div>
              </div>
            </motion.div>
          )}

          <motion.div 
            className="download-buttons"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
            viewport={{ once: true }}
          >
            <a 
              href="https://marketplace.visualstudio.com/items?itemName=aMonteSl.code-xr"
              target="_blank"
              rel="noopener noreferrer"
              className="download-button primary"
            >
              <span>📦</span>
              Install from Marketplace
            </a>

            <motion.div
              className="install-command"
              onClick={copyCommand}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.6 }}
              viewport={{ once: true }}
            >
              <span className="command-label">CLI:</span>
              <span className="command-text">ext install aMonteSl.code-xr</span>
              <button className="copy-button">
                {copied ? '✓' : '📋'}
              </button>
              {copied && (
                <motion.div
                  className="copy-tooltip"
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                  exit={{ opacity: 0, scale: 0.8 }}
                >
                  Copied!
                </motion.div>
              )}
            </motion.div>

            <a 
              href="https://github.com/aMonteSl/babiaxr-vscode"
              target="_blank"
              rel="noopener noreferrer"
              className="download-button secondary"
            >
              <span>🔗</span>
              View on GitHub
            </a>
          </motion.div>

          <motion.div 
            className="version-info"
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ duration: 0.6, delay: 0.8 }}
            viewport={{ once: true }}
          >
            <span>
              Version {loading ? '...' : formatVersion(version)} • Compatible with VS Code 1.60+
            </span>
          </motion.div>
        </motion.div>
      </motion.section>

      {/* Slide Navigation */}
      <div className="slide-navigation">
        {Array.from({ length: totalSlides }, (_, i) => (
          <button
            key={i}
            className={`nav-dot ${currentSlide === i ? 'active' : ''}`}
            onClick={() => goToSlide(i)}
            aria-label={`Go to slide ${i + 1}`}
          />
        ))}
      </div>

      {/* Slide Counter */}
      <div className="slide-counter">
        <span>{String(currentSlide + 1).padStart(2, '0')}</span>
        <span className="divider">/</span>
        <span>{String(totalSlides).padStart(2, '0')}</span>
      </div>

      {/* Floating Action Button */}
      <div className="fab-container">
        {fabOpen && <div className="fab-overlay" onClick={() => setFabOpen(false)} />}

        <AnimatePresence>
          {fabOpen && (
            <>
              <motion.a
                href="https://github.com/aMonteSl/babiaxr-vscode"
                target="_blank"
                rel="noopener noreferrer"
                className="fab-item fab-github"
                initial={{ y: 0, opacity: 0, scale: 0 }}
                animate={{ y: -70, opacity: 1, scale: 1 }}
                exit={{ y: 0, opacity: 0, scale: 0 }}
                transition={{ duration: 0.2, delay: 0.0 }}
              >
                <svg className="fab-svg" viewBox="0 0 24 24" fill="currentColor">
                  <path d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z"/>
                </svg>
                <div className="fab-tooltip">GitHub Repository</div>
              </motion.a>

              <motion.a
                href="https://marketplace.visualstudio.com/items?itemName=aMonteSl.code-xr"
                target="_blank"
                rel="noopener noreferrer"
                className="fab-item fab-vscode"
                initial={{ y: 0, opacity: 0, scale: 0 }}
                animate={{ y: -140, opacity: 1, scale: 1 }}
                exit={{ y: 0, opacity: 0, scale: 0 }}
                transition={{ duration: 0.2, delay: 0.05 }}
              >
                <svg className="fab-svg" viewBox="0 0 24 24" fill="currentColor">
                  <path d="M23.15 2.587L18.21.21a1.494 1.494 0 0 0-1.705.29l-9.46 8.63-4.12-3.128a.999.999 0 0 0-1.276.057L.327 7.261A1 1 0 0 0 .326 8.74L3.899 12 .326 15.26a1 1 0 0 0 .001 1.479L1.65 17.94a.999.999 0 0 0 1.276.057l4.12-3.128 9.46 8.63a1.492 1.492 0 0 0 1.704.29l4.942-2.377A1.5 1.5 0 0 0 24 20.06V3.939a1.5 1.5 0 0 0-.85-1.352zm-5.146 14.861L10.826 12l7.178-5.448v10.896z"/>
                </svg>
                <div className="fab-tooltip">VS Code Marketplace</div>
              </motion.a>
            </>
          )}
        </AnimatePresence>

        <button
          className={`fab-main ${fabOpen ? 'fab-open' : ''}`}
          onClick={() => setFabOpen(!fabOpen)}
          aria-label="Quick actions menu"
        >
          <div className="fab-icon">
            {fabOpen ? '×' : '+'}
          </div>
        </button>
      </div>
    </div>
  );
}

export default App;
