import React, { useRef, useState, useEffect } from 'react';
import { motion, useScroll, useTransform, useSpring, AnimatePresence } from 'framer-motion';
import './App.css';

// Importar logos de tecnologÃ­as
import NodejsLogo from './assets/logos/nodejs.svg';
import TypescriptLogo from './assets/logos/typescript.svg';
import AframeLogo from './assets/logos/aframe.png';
import BabiaXRLogo from './assets/logos/babiaxr.png';
import PythonLogo from './assets/logos/python.svg';
import WebXRLogo from './assets/logos/webxr.png';

// Importar logos de Code-XR
import CodeXRIcon from '...svg';       // Para el hero (fondo transparente)
import CodeXRIconPNG from './assets/logos/icon.png';    // Para el navbar (fondo blanco)

function App() {
  const containerRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [copied, setCopied] = useState(false);
  const [currentTextIndex, setCurrentTextIndex] = useState(0);
  const [fabOpen, setFabOpen] = useState(false); // Estado para el FAB
  const totalSlides = 6;

  // Frases rotativas para el subtÃ­tulo
  const rotatingTexts = [
    "Visualize your code metrics in Augmented and Virtual Reality.",
    "Transform static code analysis into immersive XR exploration.",
    "Experience your code in XR or on-screen â€” clear, structured, insightful.",
    "Step beyond the editor â€” analyze code with spatial context in XR.",
    "From static views to immersive XR â€” see what your code is really doing.",
    "Code metrics reimagined â€” interactively, spatially, in XR."
  ];

  // Hook principal de scroll para toda la pÃ¡gina
  const { scrollYProgress } = useScroll({
    target: containerRef,
    offset: ["start start", "end end"]
  });

  // Progress bar del scroll
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  // Efecto para cambiar el texto cada 4 segundos
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTextIndex((prevIndex) => 
        prevIndex === rotatingTexts.length - 1 ? 0 : prevIndex + 1
      );
    }, 6000); // Cambia cada 4 segundos

    return () => clearInterval(interval);
  }, [rotatingTexts.length]);

  // Detectar el slide actual basado en el scroll
  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll('.slide');
      const scrollPosition = window.scrollY + window.innerHeight * 0.3; // Cambio: usar 30% en lugar de 50%

      sections.forEach((section, index) => {
        const sectionTop = section.offsetTop;
        const sectionHeight = section.offsetHeight;

        if (scrollPosition >= sectionTop && scrollPosition < sectionTop + sectionHeight) {
          setCurrentSlide(index);
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // FunciÃ³n para navegar a un slide especÃ­fico
  const goToSlide = (slideIndex) => {
    const slides = document.querySelectorAll('.slide');
    if (slides[slideIndex]) {
      slides[slideIndex].scrollIntoView({ 
        behavior: 'smooth',
        block: 'start'
      });
    }
  };

  // FunciÃ³n para copiar el comando al portapapeles
  const copyCommand = async () => {
    try {
      await navigator.clipboard.writeText('ext install aMonteSl.code-xr');
      setCopied(true);
      setTimeout(() => setCopied(false), 2000); // Reset despuÃ©s de 2 segundos
    } catch (err) {
      // Fallback para navegadores que no soportan clipboard API
      const textArea = document.createElement('textarea');
      textArea.value = 'ext install aMonteSl.code-xr';
      document.body.appendChild(textArea);
      textArea.select();
      document.execCommand('copy');
      document.body.removeChild(textArea);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    }
  };

  // Cerrar FAB al hacer clic fuera
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (fabOpen && !event.target.closest('.fab-container')) {
        setFabOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [fabOpen]);

  return (
    <div className="App" ref={containerRef}>
      {/* Progress Bar */}
      <motion.div 
        className="progress-bar"
        style={{ scaleX }}
      />

      {/* FAB - Floating Action Button */}
      {fabOpen && <div className="fab-overlay" onClick={() => setFabOpen(false)} />}

      <div className="fab-container">
        <motion.button
          className={`fab-main ${fabOpen ? 'fab-open' : ''}`}
          onClick={() => setFabOpen(!fabOpen)}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
          animate={{ rotate: fabOpen ? 45 : 0 }}
          transition={{ duration: 0.3 }}
        >
          <span className="fab-icon">+</span>
        </motion.button>

        <AnimatePresence>
          {fabOpen && (
            <>
              <motion.a
                href="https://github.com/aMonteSl/CodeXR"
                target="_blank"
                rel="noopener noreferrer"
                className="fab-item fab-github"
                initial={{ scale: 0, y: 0, opacity: 0 }}
                animate={{ scale: 1, y: -70, opacity: 1 }}
                exit={{ scale: 0, y: 0, opacity: 0 }}
                transition={{ 
                  duration: 0.4, 
                  delay: 0.1,
                  ease: [0.4, 0, 0.2, 1],
                  type: "spring",
                  stiffness: 200,
                  damping: 20
                }}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <svg viewBox="0 0 24 24" className="fab-svg">
                  <path fill="currentColor" d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z"/>
                </svg>
                <span className="fab-tooltip">GitHub</span>
              </motion.a>

              <motion.a
                href="https://marketplace.visualstudio.com/items?itemName=aMonteSl.code-xr"
                target="_blank"
                rel="noopener noreferrer"
                className="fab-item fab-vscode"
                initial={{ scale: 0, y: 0, opacity: 0 }}
                animate={{ scale: 1, y: -140, opacity: 1 }}
                exit={{ scale: 0, y: 0, opacity: 0 }}
                transition={{ 
                  duration: 0.4, 
                  delay: 0.2,
                  ease: [0.4, 0, 0.2, 1],
                  type: "spring",
                  stiffness: 200,
                  damping: 20
                }}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <svg viewBox="0 0 24 24" className="fab-svg">
                  <path fill="currentColor" d="M23.15 2.587L18.21.21a1.494 1.494 0 0 0-1.705.29l-9.46 8.63-4.12-3.128a.999.999 0 0 0-1.276.057L.327 7.261A1 1 0 0 0 .326 8.74L3.899 12 .326 15.26a1 1 0 0 0 .001 1.479L1.65 17.94a.999.999 0 0 0 1.276.057l4.12-3.128 9.46 8.63a1.492 1.492 0 0 0 1.704.29l4.942-2.377A1.5 1.5 0 0 0 24 20.06V3.939a1.5 1.5 0 0 0-.85-1.352zm-5.146 14.861L10.826 12l7.178-5.448v10.896z"/>
                </svg>
                <span className="fab-tooltip">VS Code</span>
              </motion.a>
            </>
          )}
        </AnimatePresence>
      </div>

      {/* Slide Navigation */}
      <div className="slide-navigation">
        {Array.from({ length: totalSlides }).map((_, index) => (
          <motion.button
            key={index}
            className={`nav-dot ${currentSlide === index ? 'active' : ''}`}
            onClick={() => goToSlide(index)}
            whileHover={{ scale: 1.2 }}
            whileTap={{ scale: 0.9 }}
          />
        ))}
      </div>

      {/* Slide Counter */}
      <div className="slide-counter">
        <span>{String(currentSlide + 1).padStart(2, '0')}</span>
        <span className="divider">/</span>
        <span>{String(totalSlides).padStart(2, '0')}</span>
      </div>

      {/* Header - ACTUALIZADO CON ENLACES EXTERNOS */}
      <motion.header 
        className="header"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <div className="nav-container">
          <motion.div 
            className="logo-container"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => goToSlide(0)}
          >
            <img src={CodeXRIconPNG} alt="Code-XR" className="logo-icon" />
            <span className="logo-text">Code-XR</span>
          </motion.div>
          <nav className="nav-menu">
            {/* Enlaces de navegaciÃ³n interna */}
            <motion.button 
              onClick={() => goToSlide(0)}
              className="nav-link internal"
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              Inicio
            </motion.button>
            <motion.button 
              onClick={() => goToSlide(1)}
              className="nav-link internal"
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              CaracterÃ­sticas
            </motion.button>
            <motion.button 
              onClick={() => goToSlide(2)}
              className="nav-link internal"
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              CÃ³mo Usar
            </motion.button>
            <motion.button 
              onClick={() => goToSlide(3)}
              className="nav-link internal"
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              Beneficios
            </motion.button>
            <motion.button 
              onClick={() => goToSlide(4)}
              className="nav-link internal"
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              Descargar
            </motion.button>
            <motion.button 
              onClick={() => goToSlide(5)}
              className="nav-link internal"
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              TecnologÃ­as
            </motion.button>

            {/* Separador visual */}
            <div className="nav-separator"></div>

            {/* Enlaces externos */}
            <motion.a
              href="https://marketplace.visualstudio.com/items?itemName=aMonteSl.code-xr"
              target="_blank"
              rel="noopener noreferrer"
              className="nav-link external"
              whileHover={{ 
                scale: 1.1, 
                color: "#ff6b6b",
                textShadow: "0 0 8px rgba(255, 107, 107, 0.4)"
              }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              <span className="nav-text">Marketplace</span>
              <span className="external-icon">â†—</span>
            </motion.a>

            <motion.a
              href="https://github.com/aMonteSl/CodeXR"
              target="_blank"
              rel="noopener noreferrer"
              className="nav-link external"
              whileHover={{ 
                scale: 1.1, 
                color: "#feca57",
                textShadow: "0 0 8px rgba(254, 202, 87, 0.4)"
              }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              <span className="nav-text">GitHub</span>
              <span className="external-icon">â†—</span>
            </motion.a>
          </nav>
        </div>
      </motion.header>

      {/* Hero Slide - MANTIENE SVG TRANSPARENTE */}
      <motion.section 
        className="slide hero-slide"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <motion.div 
          className="hero-content"
          initial={{ y: 100, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 1, delay: 0.3 }}
        >
          <motion.div 
            className="hero-logo-container"
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <img src={CodeXRIcon} alt="Code-XR Logo" className="hero-logo" />
          </motion.div>

          <motion.h1 
            className="hero-title"
            initial={{ scale: 0.8 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.8, delay: 0.5 }}
          >
            <span className="brand">Code-XR</span> for VS Code
          </motion.h1>

          {/* SubtÃ­tulo animado con texto rotativo */}
          <div className="hero-subtitle-container">
            <AnimatePresence mode="wait">
              <motion.p 
                key={currentTextIndex}
                className="hero-subtitle"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ 
                  duration: 0.8,
                  ease: "easeInOut"
                }}
              >
                {rotatingTexts[currentTextIndex]}
              </motion.p>
            </AnimatePresence>
          </div>

          <motion.div
            className="install-command"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.75 }}
            onClick={copyCommand}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <span className="command-label">Quick Install:</span>
            <code className="command-text">ext install aMonteSl.code-xr</code>
            <motion.button 
              className="copy-button"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              animate={copied ? { scale: [1, 1.2, 1] } : {}}
              transition={{ duration: 0.3 }}
            >
              {copied ? 'âœ“' : 'ðŸ“‹'}
            </motion.button>

            {/* Tooltip de confirmaciÃ³n */}
            <AnimatePresence>
              {copied && (
                <motion.div
                  className="copy-tooltip"
                  initial={{ opacity: 0, y: 10, scale: 0.8 }}
                  animate={{ opacity: 1, y: 0, scale: 1 }}
                  exit={{ opacity: 0, y: -10, scale: 0.8 }}
                  transition={{ duration: 0.2 }}
                >
                  Copied!
                </motion.div>
              )}
            </AnimatePresence>
          </motion.div>

          <motion.div
            className="hero-badges"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            <span className="badge">ðŸ¥½ VR/AR</span>
            <span className="badge">ðŸ“Š Metrics</span>
            <span className="badge">âš¡ Real-time</span>
          </motion.div>

          <motion.button 
            className="cta-button"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.9 }}
            whileHover={{ 
              scale: 1.05, 
              boxShadow: "0px 10px 30px rgba(0,0,0,0.3)",
