import { useState, useEffect } from 'react';
import { getExtensionStats } from '../utils/marketplaceAPI';

export const useExtensionStats = () => {
  const [stats, setStats] = useState({
    downloads: 15847, // Valores por defecto/fallback
    installs: 3291,
    rating: 4.8,
    ratingCount: 42,
    version: '2.0.0',
    loading: true,
    error: null
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        console.log('ðŸš€ Iniciando fetch de estadÃ­sticas...');
        setStats(prev => ({ ...prev, loading: true, error: null }));

        const data = await getExtensionStats();

        if (data) {
          console.log('âœ… Datos recibidos en hook:', data);
          console.log('ðŸ“Š Downloads especÃ­ficos:', data.downloads);
          console.log('ðŸ“Š Installs especÃ­ficos:', data.installs);
          console.log('ðŸ“Š Rating especÃ­fico:', data.rating);
          console.log('ðŸ› Debug info:', data.debug);

          const newStats = {
            downloads: data.downloads || 15847, // Mantener fallback si es 0
            installs: data.installs || 3291,
            rating: data.rating ? Math.round(data.rating * 10) / 10 : 4.8,
            ratingCount: data.ratingCount || 42,
            version: data.version || '2.0.0',
            loading: false,
            error: null
          };

          console.log('ðŸ“ˆ Stats finales que se van a aplicar:', newStats);
          setStats(newStats);
        } else {
          console.error('âŒ No se recibieron datos del API');
          throw new Error('No data received');
        }
      } catch (error) {
        console.error('ðŸ’¥ Error en hook useExtensionStats:', error);
        setStats(prev => ({
          ...prev,
          loading: false,
          error: 'Failed to load stats'
        }));
      }
    };

    fetchStats();

    // Actualizar cada 10 minutos
    const interval = setInterval(fetchStats, 10 * 60 * 1000);

    return () => clearInterval(interval);
  }, []);

  // Logging adicional cuando cambian las stats
  useEffect(() => {
    console.log('ðŸ“Š Stats actualizadas en estado:', {
      downloads: stats.downloads,
      installs: stats.installs,
      rating: stats.rating,
      loading: stats.loading,
      error: stats.error
    });
  }, [stats]);

  return stats;
};
