import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion, useScroll, useSpring } from 'framer-motion';
import { useExtensionStats } from '../hooks/useExtensionStats';
import { formatNumber, formatRating, formatVersion } from '../utils/formatNumbers';

// Import technology logos
// import ReactLogo from '../assets/logos/react.svg';
// import NodeLogo from '../assets/logos/node.svg';
// import ElectronLogo from '../assets/logos/electron.svg';
// import ThreeJSLogo from '../assets/logos/threejs.svg';
// import WebXRLogo from '../assets/logos/webxr.svg';
// import JSLogo from '../assets/logos/javascript.svg';

// Import Code-XR logos
import CodeXRIconPNG from '../assets/logos/icon.png'; // ✨ USAR PNG para el header

// ✨ NUEVO: Importar componente FAB y HeroSection
import { FAB } from '../components';
import HeroSection from '../components/sections/HeroSection';

function Home() {
  const navigate = useNavigate();
  const containerRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [copied, setCopied] = useState(false);
  const [currentTextIndex, setCurrentTextIndex] = useState(0);
  const totalSlides = 6;

  // Hook para obtener estadísticas reales
  const { downloads, installs, rating, version, loading, error } = useExtensionStats();

  // ✨ DEBUGGING: Log de versión en tiempo real
  useEffect(() => {
    console.log('🏠 HOME - Stats actualizadas:', { 
      downloads, installs, rating, version, loading, error 
    });
  }, [version, loading, error]);

  // Rotating phrases for subtitle
  const rotatingTexts = [
    "Visualize your code metrics in Augmented and Virtual Reality.",
    "Transform static code analysis into immersive XR exploration.",
    "Experience your code in XR or on-screen — clear, structured, insightful.",
    "Step beyond the editor — analyze code with spatial context in XR.",
    "From static views to immersive XR — see what your code is really doing.",
    "Code metrics reimagined — interactively, spatially, in XR."
  ];

  // Main scroll hook for the entire page
  const { scrollYProgress } = useScroll({
    target: containerRef,
    offset: ["start start", "end end"]
  });

  // Progress bar for scroll
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  // Effect to change text every 6 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTextIndex((prev) => (prev + 1) % rotatingTexts.length);
    }, 6000);

    return () => clearInterval(interval);
  }, [rotatingTexts.length]);

  // Detect current slide based on scroll
  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const windowHeight = window.innerHeight;
      const newSlide = Math.round(scrollPosition / windowHeight);
      setCurrentSlide(Math.min(newSlide, totalSlides - 1));
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Function to navigate to specific slide
  const goToSlide = (slideIndex) => {
    const targetY = slideIndex * window.innerHeight;
    window.scrollTo({
      top: targetY,
      behavior: 'smooth'
    });
  };

  // Function to copy command to clipboard
  const copyCommand = async () => {
    try {
      await navigator.clipboard.writeText('ext install aMonteSl.code-xr');
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy: ', err);
    }
  };

  return (
    <div className="App" ref={containerRef}>
      {/* ✨ PROGRESS BAR */}
      <motion.div className="progress-bar" style={{ scaleX }} />

      {/* ✨ HEADER */}
      <header className="header">
        <nav className="nav-container">
          <div className="logo-container" onClick={() => goToSlide(0)}>
            <img src={CodeXRIconPNG} alt="Code-XR" className="logo-icon" />
            <span className="logo-text">Code-XR</span>
            <span className="version-badge">{formatVersion(version)}</span>
          </div>

          <div className="nav-menu">
            <button className="nav-link internal" onClick={() => goToSlide(1)}>
              <span className="nav-text">Multimedia</span>
            </button>
            <div className="nav-separator"></div>
            <button className="nav-link internal" onClick={() => goToSlide(2)}>
              <span className="nav-text">Features</span>
            </button>
            <div className="nav-separator"></div>
            <button className="nav-link internal" onClick={() => goToSlide(3)}>
              <span className="nav-text">How to Use</span>
            </button>
            <div className="nav-separator"></div>
            <button className="nav-link internal" onClick={() => goToSlide(4)}>
              <span className="nav-text">Technologies</span>
            </button>
            <div className="nav-separator"></div>
            <a 
              href="https://github.com/aMonteSl/babiaxr-vscode"
              target="_blank"
              rel="noopener noreferrer"
              className="nav-link external"
            >
              <span className="nav-text">GitHub</span>
              <span className="external-icon">↗</span>
            </a>
            <a 
              href="https://marketplace.visualstudio.com/items?itemName=aMonteSl.code-xr"
              target="_blank"
              rel="noopener noreferrer"
              className="nav-link external"
            >
              <span className="nav-text">Marketplace</span>
              <span className="external-icon">↗</span>
            </a>
          </div>
        </nav>
      </header>

      {/* ✨ SLIDE NAVIGATION */}
      <div className="slide-navigation">
        {Array.from({ length: totalSlides }, (_, i) => (
          <button
            key={i}
            className={`nav-dot ${i === currentSlide ? 'active' : ''}`}
            onClick={() => goToSlide(i)}
            aria-label={`Go to slide ${i + 1}`}
          />
        ))}
      </div>

      {/* ✨ SLIDE COUNTER */}
      <div className="slide-counter">
        {currentSlide + 1}<span className="divider">/</span>{totalSlides}
      </div>

      {/* ✨ USAR HERO SECTION COMPONENT */}
      <HeroSection 
        currentTextIndex={currentTextIndex}
        rotatingTexts={rotatingTexts}
        goToSlide={goToSlide}
      />

      {/* ✨ RESTO DE SLIDES SIN CAMBIOS... */}

      {/* Hero Multimedia Slide */}
      <div className="slide hero-multimedia-slide" id="multimedia">
        {/* ... resto del código sin cambios ... */}
      </div>

      {/* Features Slide */}
      <div className="slide features-slide" id="features">
        {/* ... resto del código sin cambios ... */}
      </div>

      {/* How to Use Slide */}
      <div className="slide howto-slide" id="howto">
        {/* ... resto del código sin cambios ... */}
      </div>

      {/* Technologies Slide */}
      <div className="slide technologies-slide" id="technologies">
        {/* ... resto del código sin cambios ... */}
      </div>

      {/* Download Slide */}
      <div className="slide download-slide" id="download">
        {/* ... resto del código sin cambios ... */}
      </div>

      {/* ✨ FAB COMPONENT */}
      <FAB />
    </div>
  );
}

export default Home;
