import React, { useRef, useState, useEffect } from 'react';
import { motion, useScroll, useTransform, useSpring, AnimatePresence } from 'framer-motion';
import './App.css';

function App() {
  const containerRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const totalSlides = 5; // Hero, Features, How to Use, Benefits, Download

  // Hook principal de scroll para toda la pÃ¡gina
  const { scrollYProgress } = useScroll({
    target: containerRef,
    offset: ["start start", "end end"]
  });

  // Progress bar del scroll
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  // Detectar el slide actual basado en el scroll
  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll('.slide');
      const scrollPosition = window.scrollY + window.innerHeight / 2;

      sections.forEach((section, index) => {
        const sectionTop = section.offsetTop;
        const sectionHeight = section.offsetHeight;

        if (scrollPosition >= sectionTop && scrollPosition < sectionTop + sectionHeight) {
          setCurrentSlide(index);
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // FunciÃ³n para navegar a un slide especÃ­fico
  const goToSlide = (slideIndex) => {
    const slides = document.querySelectorAll('.slide');
    if (slides[slideIndex]) {
      slides[slideIndex].scrollIntoView({ 
        behavior: 'smooth',
        block: 'start'
      });
    }
  };

  return (
    <div className="App" ref={containerRef}>
      {/* Progress Bar */}
      <motion.div 
        className="progress-bar"
        style={{ scaleX }}
      />

      {/* Slide Navigation Dots */}
      <div className="slide-navigation">
        {Array.from({ length: totalSlides }).map((_, index) => (
          <motion.button
            key={index}
            className={`nav-dot ${currentSlide === index ? 'active' : ''}`}
            onClick={() => goToSlide(index)}
            whileHover={{ scale: 1.2 }}
            whileTap={{ scale: 0.9 }}
          />
        ))}
      </div>

      {/* Slide Numbers */}
      <div className="slide-counter">
        <span>{String(currentSlide + 1).padStart(2, '0')}</span>
        <span className="divider">/</span>
        <span>{String(totalSlides).padStart(2, '0')}</span>
      </div>

      {/* Header/Navigation */}
      <motion.header 
        className="header"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <div className="nav-container">
          <motion.div 
            className="logo"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Code-XR
          </motion.div>
          <nav className="nav-menu">
            <motion.button 
              onClick={() => goToSlide(0)}
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              Inicio
            </motion.button>
            <motion.button 
              onClick={() => goToSlide(1)}
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              CaracterÃ­sticas
            </motion.button>
            <motion.button 
              onClick={() => goToSlide(2)}
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              CÃ³mo Usar
            </motion.button>
            <motion.button 
              onClick={() => goToSlide(3)}
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              Beneficios
            </motion.button>
            <motion.button 
              onClick={() => goToSlide(4)}
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              Descargar
            </motion.button>
          </nav>
        </div>
      </motion.header>

      {/* Slide 1: Hero Section */}
      <motion.section 
        className="slide hero-slide"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <motion.div 
          className="hero-content"
          initial={{ y: 100, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 1, delay: 0.3 }}
        >
          <motion.h1 
            className="hero-title"
            initial={{ scale: 0.8 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.8, delay: 0.5 }}
          >
            <span className="brand">Code-XR</span> for VS Code
          </motion.h1>

          <motion.p 
            className="hero-subtitle"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.7 }}
          >
            Visualize your code metrics in Augmented and Virtual Reality. 
            Transform code analysis into immersive 3D experiences.
          </motion.p>

          <motion.div
            className="install-command"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.75 }}
          >
            <span className="command-label">Quick Install:</span>
            <code className="command-text">ext install aMonteSl.code-xr</code>
          </motion.div>

          <motion.div
            className="hero-badges"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            <span className="badge">ðŸ¥½ VR/AR</span>
            <span className="badge">ðŸ“Š 3D Metrics</span>
            <span className="badge">âš¡ Real-time</span>
          </motion.div>

          <motion.button 
            className="cta-button"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.9 }}
            whileHover={{ 
              scale: 1.05, 
              boxShadow: "0px 10px 30px rgba(0,0,0,0.3)",
              background: "linear-gradient(45deg, #ff8a8a, #5ed9d1)"
            }}
            whileTap={{ scale: 0.95 }}
            onClick={() => goToSlide(1)}
          >
            Explore Features
          </motion.button>
        </motion.div>

        {/* VS Code Icon */}
        <motion.div 
          className="vscode-icon"
          animate={{
            y: [0, -15, 0],
            rotateY: [0, 10, 0],
          }}
          transition={{
            duration: 4,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        >
          ðŸ’»
        </motion.div>
      </motion.section>

      {/* Slide 2: Features Section */}
      <motion.section 
        className="slide features-slide"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true, amount: 0.3 }}
      >
        <motion.h2 
          className="section-title"
          initial={{ y: 50, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          ðŸ§  CaracterÃ­sticas Principales
        </motion.h2>

        <div className="features-grid">
          {[
            { 
              title: "VisualizaciÃ³n XR Inmersiva", 
              desc: "Explora mÃ©tricas como lÃ­neas de cÃ³digo, parÃ¡metros de funciones y complejidad en entornos 3D interactivos", 
              icon: "ðŸ¥½",
              color: "#ff6b6b"
            },
            { 
              title: "Actualizaciones en Tiempo Real", 
              desc: "Los cambios en el cÃ³digo se reflejan instantÃ¡neamente en las visualizaciones, sin recargar", 
              icon: "âš¡",
              color: "#4ecdc4"
            },
            { 
              title: "Mapeo Multidimensional", 
              desc: "Representa mÃ©tricas mediante altura, profundidad, Ã¡rea y color personalizable", 
              icon: "ðŸ“Š",
              color: "#45b7d1"
            },
            { 
              title: "IntegraciÃ³n BabiaXR + A-Frame", 
              desc: "Utiliza tecnologÃ­as avanzadas para renderizar grÃ¡ficos 3D y experiencias XR fluidas", 
              icon: "ðŸ”§",
              color: "#96ceb4"
            },
            { 
              title: "Compatibilidad Multiplataforma", 
              desc: "Funciona con Oculus, Valve Index, HTC Vive y dispositivos AR/navegadores", 
              icon: "ðŸŒ",
              color: "#feca57"
            },
            { 
              title: "IntegraciÃ³n VS Code", 
              desc: "Se adapta perfectamente a tu flujo de trabajo habitual en Visual Studio Code", 
              icon: "ðŸ’»",
              color: "#ff9ff3"
            }
          ].map((feature, index) => (
            <motion.div 
              key={index}
              className="feature-card"
              initial={{ opacity: 0, y: 50, rotateY: -10 }}
              whileInView={{ opacity: 1, y: 0, rotateY: 0 }}
              transition={{ 
                duration: 0.6, 
                delay: index * 0.1,
                type: "spring",
                stiffness: 100
              }}
              whileHover={{ 
                scale: 1.05, 
                rotateY: 5,
                boxShadow: "0 20px 40px rgba(0,0,0,0.2)"
              }}
              viewport={{ once: true, amount: 0.3 }}
              style={{ '--accent-color': feature.color }}
            >
              <motion.div 
                className="feature-icon"
                whileHover={{ 
                  scale: 1.2,
                  rotate: [0, -10, 10, -10, 0],
                }}
                transition={{ duration: 0.5 }}
              >
                {feature.icon}
              </motion.div>
              <h3>{feature.title}</h3>
              <p>{feature.desc}</p>
            </motion.div>
          ))}
        </div>
      </motion.section>

      {/* Slide 3: How to Use Section */}
      <motion.section 
        className="slide howto-slide"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true, amount: 0.5 }}
      >
        <motion.h2 
          className="section-title"
          initial={{ y: 50, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          ðŸš€ CÃ³mo Utilizar Code-XR
        </motion.h2>

        <motion.div
          className="steps-container"
          initial={{ y: 30, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          viewport={{ once: true }}
        >
          {[
            {
              step: "01",
              title: "AnÃ¡lisis XR",
              desc: "Haz clic derecho en un archivo y selecciona 'CodeXR Analyze File: XR'",
              icon: "ðŸ–±ï¸"
            },
            {
              step: "02", 
              title: "VisualizaciÃ³n 3D",
              desc: "Se abre una ventana del navegador con la visualizaciÃ³n 3D de tu cÃ³digo",
              icon: "ðŸŒ"
            },
            {
              step: "03",
              title: "Modos de NavegaciÃ³n",
              desc: "AR: Mundo real â€¢ VR: InmersiÃ³n completa â€¢ 3D: Controles de mouse",
              icon: "ðŸŽ®"
            }
          ].map((step, index) => (
            <motion.div 
              key={index}
              className="step-item"
              initial={{ opacity: 0, x: -50 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: 0.4 + index * 0.2 }}
              viewport={{ once: true }}
              whileHover={{ scale: 1.02 }}
            >
              <div className="step-number">{step.step}</div>
              <div className="step-content">
                <div className="step-icon">{step.icon}</div>
                <h3>{step.title}</h3>
                <p>{step.desc}</p>
              </div>
            </motion.div>
          ))}
        </motion.div>
      </motion.section>

      {/* Slide 4: Benefits Section */}
      <motion.section 
        className="slide benefits-slide"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true, amount: 0.5 }}
      >
        <motion.h2 
          className="section-title"
          initial={{ y: 50, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          ðŸŽ¯ Beneficios de Code-XR
        </motion.h2>

        <motion.div
          className="benefits-grid"
          initial={{ y: 30, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          viewport={{ once: true }}
        >
          {[
            {
              title: "ComprensiÃ³n Espacial",
              desc: "Visualiza la estructura del cÃ³digo en 3D, identificando Ã¡reas complejas fÃ¡cilmente",
              icon: "ðŸ§­",
              stats: "300% mÃ¡s rÃ¡pido"
            },
            {
              title: "ColaboraciÃ³n Mejorada", 
              desc: "Comparte visualizaciones XR para discusiones efectivas sobre arquitectura",
              icon: "ðŸ¤",
              stats: "50% menos bugs"
            },
            {
              title: "Flujo de Trabajo Fluido",
              desc: "IntegraciÃ³n perfecta con VS Code, sin interrumpir tu rutina de desarrollo",
              icon: "âš¡",
              stats: "100% compatible"
            }
          ].map((benefit, index) => (
            <motion.div 
              key={index}
              className="benefit-card"
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 + index * 0.2 }}
              viewport={{ once: true }}
              whileHover={{ 
                scale: 1.05,
                boxShadow: "0 25px 50px rgba(0,0,0,0.2)"
              }}
            >
              <div className="benefit-icon">{benefit.icon}</div>
              <h3>{benefit.title}</h3>
              <p>{benefit.desc}</p>
              <div className="benefit-stats">{benefit.stats}</div>
            </motion.div>
          ))}
        </motion.div>
      </motion.section>

      {/* Slide 5: Download Section */}
      <motion.section 
        className="slide download-slide"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
      >
        <motion.h2 
          className="section-title"
          initial={{ y: 50, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          Â¡Comienza Ahora!
        </motion.h2>

        <motion.div
          className="download-content"
          initial={{ y: 30, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          viewport={{ once: true }}
        >
          <p className="download-text">
            Descarga Code-XR desde el VS Code Marketplace y transforma tu experiencia de desarrollo
          </p>

          <motion.div 
            className="download-buttons"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
            viewport={{ once: true }}
          >
            <motion.a 
              href="https://marketplace.visualstudio.com/items?itemName=CodeXR"
              target="_blank"
              rel="noopener noreferrer"
              className="download-button primary"
              whileHover={{ 
                scale: 1.05, 
                boxShadow: "0px 15px 35px rgba(0,0,0,0.3)"
              }}
              whileTap={{ scale: 0.95 }}
            >
              ðŸ“¦ Descargar de VS Code Marketplace
            </motion.a>

            <motion.a 
              href="https://github.com/CodeXR-VSCode"
              target="_blank"
              rel="noopener noreferrer"
              className="download-button secondary"
              whileHover={{ 
                scale: 1.05,
                background: "rgba(255,255,255,0.2)"
              }}
              whileTap={{ scale: 0.95 }}
            >
              ðŸ’» Ver en GitHub
            </motion.a>
          </motion.div>

          <motion.div 
            className="version-info"
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ duration: 0.6, delay: 0.6 }}
            viewport={{ once: true }}
          >
            <span>VersiÃ³n 2.0.0 â€¢ Compatible con VS Code 1.60+</span>
          </motion.div>
        </motion.div>
      </motion.section>
    </div>
  );
}

export default App;
