import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import '../App.css';

// ✨ CAMBIO: Usar PNG para el header
import CodeXRIconPNG from '../assets/logos/icon.png';

// ✨ NUEVO: Importar componente FAB
import { FAB } from '../components';

function KeyFeatures() {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('overview');
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [currentSlide, setCurrentSlide] = useState(0);
  const totalSlides = 4;

  // ✨ AGREGAR: Resetear scroll al montar el componente
  useEffect(() => {
    // Forzar scroll al top inmediatamente
    window.scrollTo(0, 0);
    setCurrentSlide(0);
  }, []); // Solo al montar

  // ✨ NUEVO: Carrusel automático
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prev) => (prev + 1) % carouselImages.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [carouselImages.length]);

  // ✨ ACTUALIZAR: Detectar slide actual basado en scroll - CON THROTTLING
  useEffect(() => {
    let ticking = false;

    const handleScroll = () => {
      if (!ticking) {
        requestAnimationFrame(() => {
          const scrollTop = window.scrollY;
          const windowHeight = window.innerHeight;
          const newSlide = Math.round(scrollTop / windowHeight);
          const clampedSlide = Math.max(0, Math.min(newSlide, totalSlides - 1));

          if (clampedSlide !== currentSlide) {
            setCurrentSlide(clampedSlide);
          }

          ticking = false;
        });
        ticking = true;
      }
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, [currentSlide, totalSlides]);

  // ✨ MEJORAR: Función para navegar a slide específico
  const goToSlide = (slideIndex) => {
    const targetScroll = slideIndex * window.innerHeight;
    window.scrollTo({
      top: targetScroll,
      behavior: 'smooth'
    });
    setCurrentSlide(slideIndex);
  };

  // ✨ NUEVO: Datos para el carrusel de imágenes (placeholder)
  const carouselImages = [
    {
      id: 1,
      src: 'https://via.placeholder.com/800x500/3b82f6/ffffff?text=XR+Code+Visualization',
      caption: 'Experience your code in immersive 3D environments with real-time interaction.',
      title: 'Immersive Code Exploration'
    },
    {
      id: 2,
      src: 'https://via.placeholder.com/800x500/06b6d4/ffffff?text=3D+Metrics+View',
      caption: 'Visualize complexity metrics through dynamic 3D representations.',
      title: '3D Metrics Visualization'
    },
    {
      id: 3,
      src: 'https://via.placeholder.com/800x500/10b981/ffffff?text=AR+Code+Analysis',
      caption: 'Analyze code quality with augmented reality overlays and insights.',
      title: 'Augmented Reality Analysis'
    },
    {
      id: 4,
      src: 'https://via.placeholder.com/800x500/f59e0b/ffffff?text=Team+Collaboration',
      caption: 'Share and explore code visualizations with your team in real-time.',
      title: 'Team Collaboration in XR'
    }
  ];

  // ✨ NUEVO: Datos organizados por categorías
  const featureCategories = {
    overview: {
      title: 'Overview',
      icon: '🎯',
      features: [
        {
          title: 'Interactive Visualization',
          icon: '🔍',
          color: '#ff6b6b',
          description: 'Explore your code in an immersive 3D environment with dynamic visualizations.',
          highlights: ['Real-time 3D rendering', 'Interactive navigation', 'Dynamic feedback']
        },
        {
          title: 'Real-time Metrics',
          icon: '📊',
          color: '#4ecdc4',
          description: 'Instant analysis of complexity, code quality, and design patterns.',
          highlights: ['Cyclomatic complexity', 'Quality indicators', 'Performance hotspots']
        },
        {
          title: 'XR Experience',
          icon: '🥽',
          color: '#45b7d1',
          description: 'Compatible with AR/VR devices for total immersion.',
          highlights: ['WebXR compatibility', 'Multi-device support', 'Desktop fallback']
        }
      ]
    },
    integration: {
      title: 'Integration',
      icon: '⚡',
      features: [
        {
          title: 'Seamless VS Code Integration',
          icon: '🔧',
          color: '#96ceb4',
          description: 'One-click activation directly from your favorite editor.',
          highlights: ['Context menu integration', 'Hotkey support', 'Auto file analysis']
        },
        {
          title: 'Zero Configuration',
          icon: '🚀',
          color: '#feca57',
          description: 'No additional setup required - works out of the box.',
          highlights: ['Instant activation', 'Auto-detection', 'Smart defaults']
        }
      ]
    },
    collaboration: {
      title: 'Collaboration',
      icon: '🤝',
      features: [
        {
          title: 'Team Sharing',
          icon: '🔄',
          color: '#ff9ff3',
          description: 'Share XR visualizations with your team for better code reviews.',
          highlights: ['Shareable sessions', 'Team exploration', 'Export capabilities']
        },
        {
          title: 'Enhanced Communication',
          icon: '💬',
          color: '#a78bfa',
          description: 'Improve team communication with spatial code understanding.',
          highlights: ['Visual discussions', 'Shared context', 'Better reviews']
        }
      ]
    }
  };

  const goToImage = (index) => {
    setCurrentImageIndex(index);
  };

  const nextImage = () => {
    setCurrentImageIndex((prev) => (prev + 1) % carouselImages.length);
  };

  const prevImage = () => {
    setCurrentImageIndex((prev) => (prev - 1 + carouselImages.length) % carouselImages.length);
  };

  return (
    <div className="key-features-page">
      {/* Header */}
      <motion.header className="header">
        <div className="nav-container">
          <motion.div 
            className="logo-container"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => navigate('/')}
          >
            <img src={CodeXRIconPNG} alt="Code-XR" className="logo-icon" />
            <span className="logo-text">Code-XR</span>
          </motion.div>

          <nav className="nav-menu">
            <button 
              className="nav-link internal" 
              onClick={() => navigate('/')}
            >
              ← Back to Home
            </button>
            <div className="nav-separator"></div>
            <a 
              href="https://marketplace.visualstudio.com/items?itemName=aMonteSl.code-xr" 
              className="nav-link external"
              target="_blank"
              rel="noopener noreferrer"
            >
              <span className="nav-text">Install</span>
              <span className="external-icon">↗</span>
            </a>
            <a 
              href="https://github.com/aMonteSl/babiaxr-vscode" 
              className="nav-link external"
              target="_blank"
              rel="noopener noreferrer"
            >
              <span className="nav-text">GitHub</span>
              <span className="external-icon">↗</span>
            </a>
          </nav>
        </div>
      </motion.header>

      {/* ✨ NUEVO: Hero Section con multimedia integrada (Slide 1) */}
      <motion.section className="slide hero-multimedia-slide">
        <div className="hero-multimedia-container">
          {/* ✨ Header del Hero */}
          <motion.div
            className="hero-multimedia-header"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <h1 className="key-features-title">
              <span className="brand">Key</span> Features
            </h1>
            <p className="key-features-subtitle">
              Discover how Code-XR transforms your development experience with cutting-edge XR technology
            </p>
          </motion.div>

          {/* ✨ Contenido multimedia en dos columnas */}
          <div className="multimedia-content-grid">

            {/* ✨ Video Section */}
            <motion.div
              className="video-section-hero"
              initial={{ opacity: 0, x: -50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 0.3 }}
            >
              <h3 className="multimedia-section-title">
                <span className="section-icon">🎬</span>
                See in Action
              </h3>
              <div className="video-wrapper-hero">
                <div className="video-placeholder">
                  <div className="video-placeholder-content">
                    <div className="play-button">
                      <span>▶</span>
                    </div>
                    <h4>Watch Code-XR Demo</h4>
                    <p>Experience immersive code analysis</p>
                    <div className="video-badge">Coming Soon</div>
                  </div>
                </div>
              </div>
            </motion.div>

            {/* ✨ Carrusel Section */}
            <motion.div
              className="carousel-section-hero"
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 0.5 }}
            >
              <h3 className="multimedia-section-title">
                <span className="section-icon">📸</span>
                Experience Gallery
              </h3>

              <div className="carousel-wrapper-hero">
                <motion.button 
                  className="carousel-nav prev"
                  onClick={prevImage}
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                >
                  ←
                </motion.button>

                <div className="carousel-content-hero">
                  <AnimatePresence mode="wait">
                    <motion.div
                      key={currentImageIndex}
                      className="carousel-slide-hero"
                      initial={{ opacity: 0, x: 100 }}
                      animate={{ opacity: 1, x: 0 }}
                      exit={{ opacity: 0, x: -100 }}
                      transition={{ duration: 0.5 }}
                    >
                      <div className="carousel-image-wrapper-hero">
                        <img 
                          src={carouselImages[currentImageIndex].src} 
                          alt={carouselImages[currentImageIndex].title}
                          className="carousel-image"
                        />
                        <div className="image-overlay">
                          <h4>{carouselImages[currentImageIndex].title}</h4>
                        </div>
                      </div>
                      <div className="carousel-caption-hero">
                        <p>{carouselImages[currentImageIndex].caption}</p>
                      </div>
                    </motion.div>
                  </AnimatePresence>
                </div>

                <motion.button 
                  className="carousel-nav next"
                  onClick={nextImage}
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                >
                  →
                </motion.button>
              </div>

              <div className="carousel-indicators">
                {carouselImages.map((_, index) => (
                  <button
                    key={index}
                    className={`indicator ${index === currentImageIndex ? 'active' : ''}`}
                    onClick={() => goToImage(index)}
                  />
                ))}
              </div>
            </motion.div>

          </div>
        </div>

        {/* ✨ Flecha para siguiente sección */}
        <motion.div 
          className="next-section-arrow"
          onClick={() => goToSlide(1)}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5, delay: 1 }}
        >
          <motion.div 
            className="arrow-icon"
            animate={{ y: [0, 8, 0] }}
            transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
          >
            ↓
          </motion.div>
        </motion.div>
      </motion.section>

      {/* ✨ Features Tabs (ahora Slide 2) */}
      <motion.section className="slide features-tabs-slide">
        <div className="tabs-container">
          <motion.div 
            className="tabs-header"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2>Detailed Features</h2>
            <div className="tabs-navigation">
              {Object.entries(featureCategories).map(([key, category]) => (
                <button
                  key={key}
                  className={`tab-button ${activeTab === key ? 'active' : ''}`}
                  onClick={() => setActiveTab(key)}
                >
                  <span className="tab-icon">{category.icon}</span>
                  <span className="tab-text">{category.title}</span>
                </button>
              ))}
            </div>
          </motion.div>

          <AnimatePresence mode="wait">
            <motion.div
              key={activeTab}
              className="tab-content"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.4 }}
            >
              <div className="features-grid-compact">
                {featureCategories[activeTab].features.map((feature, index) => (
                  <motion.div
                    key={feature.title}
                    className="feature-card-compact"
                    style={{ '--feature-color': feature.color }}
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.4, delay: index * 0.1 }}
                  >
                    <div className="feature-compact-header">
                      <div 
                        className="feature-compact-icon"
                        style={{ backgroundColor: `${feature.color}20`, color: feature.color }}
                      >
                        {feature.icon}
                      </div>
                      <h3>{feature.title}</h3>
                    </div>
                    <p className="feature-compact-description">{feature.description}</p>
                    <div className="feature-highlights">
                      {feature.highlights.map((highlight, i) => (
                        <span key={i} className="highlight-tag">
                          {highlight}
                        </span>
                      ))}
                    </div>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          </AnimatePresence>
        </div>

        {/* ✨ Flecha para slide 3 */}
        <motion.div 
          className="next-section-arrow"
          onClick={() => goToSlide(2)}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5, delay: 1 }}
        >
          <motion.div 
            className="arrow-icon"
            animate={{ y: [0, 8, 0] }}
            transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
          >
            ↓
          </motion.div>
        </motion.div>
      </motion.section>

      {/* ✨ Technical Specifications (ahora Slide 3) */}
      <motion.section className="slide tech-specs-slide">
        <div className="specs-container">
          <motion.h2 
            className="specs-title"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            Technical Specifications
          </motion.h2>

          <div className="specs-grid">
            <motion.div 
              className="spec-card"
              initial={{ opacity: 0, x: -30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: 0.1 }}
              viewport={{ once: true }}
            >
              <h4>🎮 XR Compatibility</h4>
              <ul>
                <li>WebXR 1.0 Standard</li>
                <li>Oculus Quest/Rift Support</li>
                <li>HTC Vive Compatible</li>
                <li>Mobile AR Ready</li>
              </ul>
            </motion.div>

            <motion.div 
              className="spec-card"
              initial={{ opacity: 0, x: 30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
            >
              <h4>⚙️ System Requirements</h4>
              <ul>
                <li>VS Code 1.60+</li>
                <li>Node.js 14+</li>
                <li>Modern WebGL Browser</li>
                <li>4GB RAM Minimum</li>
              </ul>
            </motion.div>

            <motion.div 
              className="spec-card"
              initial={{ opacity: 0, x: -30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: 0.3 }}
              viewport={{ once: true }}
            >
              <h4>📊 Supported Metrics</h4>
              <ul>
                <li>Cyclomatic Complexity</li>
                <li>Code Coverage</li>
                <li>Technical Debt</li>
                <li>Dependency Analysis</li>
              </ul>
            </motion.div>

            <motion.div 
              className="spec-card"
              initial={{ opacity: 0, x: 30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              viewport={{ once: true }}
            >
              <h4>🔧 Integration</h4>
              <ul>
                <li>Git Integration</li>
                <li>ESLint Support</li>
                <li>TypeScript Ready</li>
                <li>Multi-language Support</li>
              </ul>
            </motion.div>
          </div>
        </div>

        {/* ✨ Flecha para slide 4 */}
        <motion.div 
          className="next-section-arrow"
          onClick={() => goToSlide(3)}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5, delay: 1 }}
        >
          <motion.div 
            className="arrow-icon"
            animate={{ y: [0, 8, 0] }}
            transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
          >
            ↓
          </motion.div>
        </motion.div>
      </motion.section>

      {/* ✨ Coming Soon (ahora Slide 4 - final) */}
      <motion.section className="slide coming-soon-slide">
        <div className="coming-soon-container">
          <motion.div
            className="coming-soon-header"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <div className="coming-soon-badge">
              <span className="badge-icon">🚀</span>
              <span className="badge-text">Coming Soon</span>
            </div>
            <h2>Future Roadmap</h2>
            <p>Exciting features and improvements planned for upcoming releases</p>
          </motion.div>

          <div className="roadmap-grid">
            <motion.div
              className="roadmap-card"
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.1 }}
              viewport={{ once: true }}
            >
              <div className="roadmap-icon">
                <span>🌐</span>
              </div>
              <div className="roadmap-content">
                <h3>Multi-Language Support</h3>
                <p>
                  Expand beyond JavaScript and TypeScript to analyze Python, Java, C#, 
                  Go, Rust, and more programming languages with native syntax support.
                </p>
                <div className="roadmap-status">
                  <span className="status-dot status-planned"></span>
                  <span className="status-text">In Planning</span>
                </div>
              </div>
            </motion.div>

            <motion.div
              className="roadmap-card"
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
            >
              <div className="roadmap-icon">
                <span>📈</span>
              </div>
              <div className="roadmap-content">
                <h3>Advanced Metrics</h3>
                <p>
                  Beyond Cyclomatic Complexity: Code maintainability index, 
                  Halstead metrics, LCOM cohesion, and custom metric definitions.
                </p>
                <div className="roadmap-status">
                  <span className="status-dot status-development"></span>
                  <span className="status-text">In Development</span>
                </div>
              </div>
            </motion.div>

            <motion.div
              className="roadmap-card"
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.3 }}
              viewport={{ once: true }}
            >
              <div className="roadmap-icon">
                <span>📁</span>
              </div>
              <div className="roadmap-content">
                <h3>Project-Level Analysis</h3>
                <p>
                  Analyze entire directories and projects with hierarchical visualization, 
                  cross-file dependencies, and architecture insights.
                </p>
                <div className="roadmap-status">
                  <span className="status-dot status-research"></span>
                  <span className="status-text">Research Phase</span>
                </div>
              </div>
            </motion.div>

            <motion.div
              className="roadmap-card"
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              viewport={{ once: true }}
            >
              <div className="roadmap-icon">
                <span>⚙️</span>
              </div>
              <div className="roadmap-content">
                <h3>Dynamic XR Configuration</h3>
                <p>
                  Real-time parameter adjustment within XR sessions through immersive menus, 
                  gesture controls, and voice commands.
                </p>
                <div className="roadmap-status">
                  <span className="status-dot status-planned"></span>
                  <span className="status-text">Conceptual</span>
                </div>
              </div>
            </motion.div>
          </div>

          <motion.div
            className="newsletter-section"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            viewport={{ once: true }}
          >
            <div className="newsletter-content">
              <h3>Stay Updated</h3>
              <p>Get notified when these exciting features become available</p>
              <div className="newsletter-form">
                <button className="newsletter-button" disabled>
                  <span>📧</span>
                  <span>Join Waitlist</span>
                  <span className="soon-label">Soon</span>
                </button>
              </div>
              <p className="newsletter-note">
                Follow our progress on <a href="https://github.com/aMonteSl/babiaxr-vscode" target="_blank" rel="noopener noreferrer">GitHub</a> 
                for the latest updates
              </p>
            </div>
          </motion.div>

          {/* ✨ CTA Final */}
          <motion.div
            className="key-features-cta-final"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
            viewport={{ once: true }}
          >
            <h2>Ready to Experience Code-XR?</h2>
            <p>Transform your code analysis workflow with immersive XR visualizations</p>
            <div className="cta-buttons">
              <a 
                href="https://marketplace.visualstudio.com/items?itemName=aMonteSl.code-xr"
                className="cta-button primary"
                target="_blank"
                rel="noopener noreferrer"
              >
                Install Extension
              </a>
              <button 
                className="cta-button secondary"
                onClick={() => navigate('/')}
              >
                View Demo
              </button>
            </div>
          </motion.div>
        </div>
      </motion.section>

      {/* ✨ Navegación ahora con 4 slides */}
      <div className="slide-navigation">
        {Array.from({ length: 4 }, (_, index) => (
          <div
            key={index}
            className={`nav-dot ${currentSlide === index ? 'active' : ''}`}
            onClick={() => goToSlide(index)}
          />
        ))}
      </div>

      {/* ✨ Contador ahora con 4 slides */}
      <div className="slide-counter">
        <span>{String(currentSlide + 1).padStart(2, '0')}</span>
        <span className="divider">/</span>
        <span>04</span>
      </div>

      {/* FAB */}
      <FAB />
    </div>
  );
}

export default KeyFeatures;
