import { useState, useEffect } from 'react';
import { getExtensionStats } from '../utils/marketplaceAPI';

export const useExtensionStats = () => {
  const [stats, setStats] = useState({
    downloads: 15847, // Valores por defecto/fallback
    installs: 3291,
    rating: 4.8,
    ratingCount: 42,
    version: '2.0.0',
    loading: true,
    error: null
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        setStats(prev => ({ ...prev, loading: true, error: null }));
        const data = await getExtensionStats();

        if (data) {
          setStats({
            downloads: data.downloads,
            installs: data.installs,
            rating: Math.round(data.rating * 10) / 10, // Redondear a 1 decimal
            ratingCount: data.ratingCount,
            version: data.version,
            loading: false,
            error: null
          });
        } else {
          throw new Error('No data received');
        }
      } catch (error) {
        console.error('Failed to fetch extension stats:', error);
        setStats(prev => ({
          ...prev,
          loading: false,
          error: 'Failed to load stats'
        }));
      }
    };

    fetchStats();

    // Actualizar cada 5 minutos
    const interval = setInterval(fetchStats, 5 * 60 * 1000);

    return () => clearInterval(interval);
  }, []);

  return stats;
};
