import { useState, useEffect } from 'react';
import { getExtensionStats } from '../utils/marketplaceAPI';

export const useExtensionStats = () => {
  const [stats, setStats] = useState({
    downloads: 120, // Valores reales que mencionaste
    installs: 38,
    rating: 4.8,
    ratingCount: 42,
    version: '2.0.0',
    loading: true,
    error: null
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        console.log('ðŸš€ Iniciando fetch de estadÃ­sticas combinado...');
        setStats(prev => ({ ...prev, loading: true, error: null }));

        const data = await getExtensionStats();

        if (data) {
          console.log('âœ… Datos finales recibidos:', data);

          const newStats = {
            // Solo actualizar si los valores son mayores a 0 y diferentes de fallback
            downloads: (data.downloads && data.downloads > 0) ? data.downloads : stats.downloads,
            installs: (data.installs && data.installs > 0) ? data.installs : stats.installs,
            rating: (data.rating && data.rating > 0) ? Math.round(data.rating * 10) / 10 : stats.rating,
            ratingCount: (data.ratingCount && data.ratingCount > 0) ? data.ratingCount : stats.ratingCount,
            version: data.version || stats.version,
            loading: false,
            error: null
          };

          console.log('ðŸ“ˆ Stats aplicadas (solo si son vÃ¡lidas):', newStats);
          setStats(newStats);
        } else {
          console.warn('âš ï¸ No se recibieron datos, manteniendo valores actuales');
          setStats(prev => ({ ...prev, loading: false }));
        }
      } catch (error) {
        console.error('ðŸ’¥ Error en hook:', error);
        setStats(prev => ({
          ...prev,
          loading: false,
          error: 'Failed to load stats'
        }));
      }
    };

    fetchStats();

    // Actualizar cada 15 minutos para datos mÃ¡s frescos
    const interval = setInterval(fetchStats, 15 * 60 * 1000);

    return () => clearInterval(interval);
  }, []);

  return stats;
};
