import React, { useRef } from 'react';
import { motion, useScroll, useTransform, useSpring } from 'framer-motion';
import './App.css';

function App() {
  const containerRef = useRef(null);
  const heroRef = useRef(null);
  const servicesRef = useRef(null);

  // Hook principal de scroll para toda la p√°gina
  const { scrollYProgress } = useScroll({
    target: containerRef,
    offset: ["start start", "end end"]
  });

  // Hook de scroll espec√≠fico para el hero
  const { scrollYProgress: heroScrollProgress } = useScroll({
    target: heroRef,
    offset: ["start end", "end start"]
  });

  // Hook de scroll para los servicios
  const { scrollYProgress: servicesScrollProgress } = useScroll({
    target: servicesRef,
    offset: ["start end", "end start"]
  });

  // Transformaciones basadas en scroll
  const backgroundY = useTransform(scrollYProgress, [0, 1], ["0%", "100%"]);
  const heroY = useTransform(heroScrollProgress, [0, 1], ["0%", "-50%"]);
  const heroOpacity = useTransform(heroScrollProgress, [0, 0.5, 1], [1, 0.8, 0]);
  const heroScale = useTransform(heroScrollProgress, [0, 1], [1, 0.8]);

  // Efecto de parallax suave para los servicios
  const servicesY = useTransform(servicesScrollProgress, [0, 1], ["-20%", "20%"]);

  // Progress bar del scroll
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  return (
    <div className="App" ref={containerRef}>
      {/* Progress Bar */}
      <motion.div 
        className="progress-bar"
        style={{ scaleX }}
      />

      {/* Background parallax effect */}
      <motion.div
        className="background-overlay"
        style={{ y: backgroundY }}
      />

      {/* Header/Navigation */}
      <motion.header 
        className="header"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <div className="nav-container">
          <motion.div 
            className="logo"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Code-XR
          </motion.div>
          <nav className="nav-menu">
            <motion.a 
              href="#inicio" 
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              Inicio
            </motion.a>
            <motion.a 
              href="#servicios" 
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              Servicios
            </motion.a>
            <motion.a 
              href="#sobre-nosotros" 
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              Sobre Nosotros
            </motion.a>
            <motion.a 
              href="#contacto" 
              whileHover={{ scale: 1.1, color: "#4ecdc4" }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              Contacto
            </motion.a>
          </nav>
        </div>
      </motion.header>

      {/* Hero Section con efectos de scroll */}
      <motion.section 
        ref={heroRef}
        className="hero"
        style={{
          y: heroY,
          opacity: heroOpacity,
          scale: heroScale,
        }}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <motion.div 
          className="hero-content"
          initial={{ y: 100, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 1, delay: 0.3 }}
        >
          <motion.h1 
            className="hero-title"
            initial={{ scale: 0.8 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.8, delay: 0.5 }}
          >
            Bienvenido a <span className="brand">Code-XR</span>
          </motion.h1>

          <motion.p 
            className="hero-subtitle"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.7 }}
          >
            Desarrollamos experiencias de Realidad Extendida que transforman tu negocio
          </motion.p>

          <motion.button 
            className="cta-button"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.9 }}
            whileHover={{ 
              scale: 1.05, 
              boxShadow: "0px 10px 30px rgba(0,0,0,0.3)",
              background: "linear-gradient(45deg, #ff8a8a, #5ed9d1)"
            }}
            whileTap={{ scale: 0.95 }}
          >
            Descubre Nuestros Servicios
          </motion.button>
        </motion.div>

        {/* Floating elements */}
        <motion.div 
          className="floating-element"
          animate={{
            y: [0, -20, 0],
            rotate: [0, 5, 0],
          }}
          transition={{
            duration: 6,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />
      </motion.section>

      {/* Services Section con parallax */}
      <motion.section 
        ref={servicesRef}
        className="services"
        style={{ y: servicesY }}
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true, amount: 0.3 }}
      >
        <motion.h2 
          className="section-title"
          initial={{ y: 50, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          Nuestros Servicios
        </motion.h2>

        <div className="services-grid">
          {[
            { 
              title: "Realidad Virtual", 
              desc: "Experiencias inmersivas que transportan a tus usuarios a mundos digitales", 
              icon: "ü•Ω" 
            },
            { 
              title: "Realidad Aumentada", 
              desc: "Superpone elementos digitales al mundo real para experiencias h√≠bridas", 
              icon: "üì±" 
            },
            { 
              title: "Desarrollo Web XR", 
              desc: "Aplicaciones web con tecnolog√≠as WebXR accesibles desde cualquier dispositivo", 
              icon: "üåê" 
            }
          ].map((service, index) => (
            <motion.div 
              key={index}
              className="service-card"
              initial={{ opacity: 0, y: 50, rotateY: -10 }}
              whileInView={{ opacity: 1, y: 0, rotateY: 0 }}
              transition={{ 
                duration: 0.6, 
                delay: index * 0.2,
                type: "spring",
                stiffness: 100
              }}
              whileHover={{ 
                scale: 1.05, 
                rotateY: 10,
                z: 50,
                boxShadow: "0 20px 40px rgba(0,0,0,0.2)"
              }}
              viewport={{ once: true, amount: 0.3 }}
            >
              <motion.div 
                className="service-icon"
                whileHover={{ 
                  scale: 1.2,
                  rotate: [0, -10, 10, -10, 0],
                }}
                transition={{ duration: 0.5 }}
              >
                {service.icon}
              </motion.div>
              <h3>{service.title}</h3>
              <p>{service.desc}</p>
            </motion.div>
          ))}
        </div>
      </motion.section>

      {/* About Section mejorada */}
      <motion.section 
        className="about"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true, amount: 0.5 }}
      >
        <motion.h2 
          className="section-title"
          initial={{ y: 50, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          Sobre Code-XR
        </motion.h2>

        <motion.div
          initial={{ y: 30, opacity: 0 }}
